//   Result after playing around with warped FBMs for a few hours
//   Based on: https://iquilezles.org/articles/warp

#define PI    3.141592654
#define TAU   (2.0*PI)
#define TTIME (iTime*TAU)

const mat2 frot = mat2(0.80, 0.60, -0.60, 0.80);

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

float noise(vec2 p) {
  float a = sin(p.x);
  float b = sin(p.y);
  float c = 0.5 + 0.5*cos(p.x + p.y);
  float d = mix(a, b, c);
  return d;
}

float fbm(vec2 p) {    
  float f = 0.0;
  float a = 1.0;
  float s = 0.0;
  float m = 2.0;
  for (int x = 0; x < 4; ++x) {
    f += a*noise(p); p = frot*p*m;
    m += 0.01;
    s += a;
    a *= 0.5;
  }
  return f/s;
}

float warp(vec2 p, out vec2 v, out vec2 w) {

  vec2 vx = vec2(0.0, 0.0);
  vec2 vy = vec2(3.2, 1.3);

  vec2 wx = vec2(1.7, 9.2);
  vec2 wy = vec2(8.3, 2.8);

  rot(vx, TTIME/1000.0);
  rot(vy, TTIME/900.0);

  rot(wx, TTIME/800.0);
  rot(wy, TTIME/700.0);

  v = vec2(fbm(p + vx), fbm(p + vy));

  w = vec2(fbm(p + 3.0*v + wx), fbm(p + 3.0*v + wy));

  return fbm(p + 2.25*w);
}

vec3 normal(vec2 p) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(0.0001, 0);

  vec3 n;
  n.x = warp(p + e.xy, v, w) - warp(p - e.xy, v, w);
  n.y = 2.0*e.x;
  n.z = warp(p + e.yx, v, w) - warp(p - e.yx, v, w);

  return normalize(n);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= iResolution.x/iResolution.y;

  p += TTIME*0.01;
  p *= 2.0;
  vec3 col = vec3(1.0);

  vec2 v;
  vec2 w;

  float f = warp(p, v, w);
  vec3 n = normal(p);

  vec3 lig = normalize(vec3(0., 0.2, -0.4));
  rot(lig.xy, TTIME/10.0);
  float dif = max(dot(lig, n), 0.5);

  const vec3 col1 = vec3(0.1, 0.7, 0.8);
  const vec3 col2 = vec3(0.7, 0.3, 0.5);

  col = pow(dif, 0.75)*tanh(pow(abs(f + 0.5), 1.5)) + (length(v)*col1 + length(w)*col2);

  fragColor = vec4(col, 1.0);
}

vec2 N(float angle) {
	return vec2(sin(angle), cos(angle));
}

// Rotate around an arbitrary line by the given angle
vec2 rotate(vec2 uv, vec2 center, float angle, bool side) {

  	// reflects a point to the other side of a line. Distance to the line
   	// is given by dot(uv, n) where n is the orientation of the line
   	// min/max makes sure that the only one side of the line will be reflected

    vec2 n = N(angle * 3.14159);
    float d = dot(uv - center, n);

    if (side) {
	    uv -= n * max(0., d) * 2.;
    } else {
        uv -= n * min(0., d) * 2.;
    }
    return uv;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {    

    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    // zoom out
    uv *= 1.25;

    // mirror on y axis. This adds the folded line to the lower left corner also
    uv.x = abs(uv.x);

    // the original curve lies on the zero line so shift the 
    // the whole thing upper in order to get it in the middle
    uv.y += tan((5./6.)*3.14159)*.5;

    // reflect the line by rotating uv around a line passing through (.5, 0)
    // by 5/6 degrees. This adds the line segment to the lower right corner alse
    uv = rotate(uv, vec2(.5, 0), (5./6.), true);

	// folds the space to create the upside down 'V' shapes
    float scale = 1.; // keeps track how many times uv space is folded
    uv.x += 0.5; // undo the first shift in the loop to keep the fractal in the middle
    for (int i=0; i<4; i++) {

        // resets the uv coordinates before folding the next upside down 'V' shape
        uv *= 3.;
        scale *= 3.;
        uv.x -= 1.5; // shift the origo in the middle of the line segment

        // this widens the line segment to 3 units of which the centermost is folded
        uv.x = abs(uv.x); // mirror around the center
        uv.x -= .5; // add .5 to the both ends of the line to make it 3 units wide

        uv = rotate(uv, vec2(0., 0.0) ,(2./3.), false);    	
    }

    // Draw the line by calculating distance to a line lying on the y axis and 
    // which x position ranges from -1 to 1
    float d = length(uv - vec2(clamp(uv.x, -1., 1.), 0.));

    // make the fractal more trippy by making it go inside out 
    // and rotating by time
    uv *= sin(iTime * 0.3);
    uv = rotate(uv, vec2(0., 0.), sin(iTime*0.5), true);

    // smooths out the line segment so its thick and narrower.
    // Division by iResolution.y makes sure its resolution independent and always 1 pixel wide
    col += smoothstep(1./iResolution.y, 0., d/scale); 

    uv /= scale;

    // let the magic happen
    col += texture(iChannel0, uv*3.2-iTime*0.2).rgb;

    fragColor = vec4(col,1.0);
}

#define PI 3.14159265
#define MAXIT 128
#define EPSILON 0.001

#define minx4(a, b) ((a.w) < (b.w) ? (a) : (b))
#define minx2(a, b) ((a.x) < (b.x) ? (a) : (b))

#define MAT0 0.0
#define MAT1 1.0

mat3 rotX(float a)
{
    return mat3(
        1., 0., 0.,
        0., cos(a), -sin(a),
        0., sin(a), cos(a)
    );
}

mat3 rotY(float a)
{
    return mat3(
        cos(a), 0.0, -sin(a),
        0., 1., 0.,
        sin(a), 0.0, cos(a)
    );
}

mat3 rotZ(float a)
{
    return mat3(
        cos(a), -sin(a), 0.,
        sin(a), cos(a), 0.,
        0., 0., 1.
    );
}

float t;

float sphere(vec3 r, float a)
{
    return length(r) - a;
}

float torus(vec3 r, vec2 a)
{
    a.y += sin(length(r.xy + t) * 7.) * 0.09;
    vec2 p = vec2(length(r.xz) - a.x, r.y);
    return length(p) - a.y;
}

float plane(vec3 r, vec3 o, vec3 n) {
    return dot(r - o, n);
}

float box(vec3 r, vec3 a)
{
    vec3 p = abs(r) - a;
    return length(max(p - p.x * 0.4 - p.y * 0.4, 0.)) + min(max(p.x - p.y * 0.5, max(p.y, p.z) - p.x * 0.3), 0.);
}

float shade(vec3 n, vec3 rd)
{
    return clamp(max(dot(n, -rd), 0.) + 1., 0., 1.);
}

vec3 fog(float z, vec3 col, vec3 fogCol)
{
    return mix(fogCol, col, exp(-z));
}

mat3 obj;

vec2 map(vec3 r)
{
    obj = rotZ((-t + r.z * 0.2) * 0.3) * rotY(1. * t);

    vec2 b1 = vec2(
        box(obj * obj * (r) + vec3(0., sin(2. * t) * 1., 0.), vec3(.75, .5, .5)),
        MAT0
    );

    vec2 t1 = vec2(
        torus(- obj *r, vec2(2.0, 0.1)),
        MAT1
    );

    return minx2(b1, t1);
}

float hash(vec2 r) {
    return fract(sin(dot(r, vec2(15.5921, 96.654654))) * 23626.3663);
}

vec3 matCol(vec2 o)
{
    if (o.y == MAT0)
        return normalize(vec3(1., 0.5, .0));

    if (o.y == MAT1)
        return normalize(vec3(0., 1.7, 0.0));

    return vec3(0.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    t = iTime;

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    mat3 cam = rotY(t * 0. - PI);

    vec3 ro = vec3(0., 0., -3.0);
    vec3 rd = cam * normalize(vec3(uv * 2., -1.));
    vec3 r = ro;

    vec3 bcol = normalize((vec3(1., 0., 1.0) + 0.5) * (uv.y + 0.3) * (sin(t) + 1.) * 1.5 + vec3(0., 0.5, 1.0)) * 1.5 + 0.4;
    vec4 col = vec4(0.);
    col.rgb = bcol;

    vec2 c = vec2(10000., MAT0);

    float sh = 1.;

    float glow = 0.;

    vec3 gcol = bcol;

    for (int i = 0; i < MAXIT; ++i) {
        vec2 d = map(r);
        float z = length(r - ro);

        glow += exp(-d.x * 5.);
        gcol += matCol(d) * exp(-d.x * 1.);

        if (d.x < EPSILON) {
            col.rgb = mix(col.rgb, matCol(d), shade(normalize(r), rd));
            col.rgb = fog(z * 0.1, col.rgb, bcol);
            break;
        }

        d.x *= 0.7 - 0.1 * hash(uv);
        r += rd * clamp(d.x, -0.5, 0.5) * 0.7;

        sh = (float(i) / float(MAXIT));
    }

    col.rgb *= exp(-sh * .9);
    col.rgb = mix(col.rgb, gcol, glow * 0.005);

    fragColor = vec4(col.rgb, 1.);
}

vec2 random(vec2 uv)
{
    return fract(vec2(dot(uv, vec2(435.6,534.3)), dot(uv, vec2(358.463,246.3))) );
}

float noise(vec2 uv)
{

    //get decimal and integer portions from position
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    //randomize each position around current position
    vec2 a = random(i);
    vec2 b = random(i + vec2(1.0, 0.0));
    vec2 c = random(i + vec2(0.0, 1.0));
    vec2 d = random(i + vec2(1.0, 1.0));

    //interpolate decimal position
    vec2 m = smoothstep(0.0,1.0,f);

    //apply interpolation to 4 corners and return result.
    return mix(mix(dot(a,f), dot(b, f-vec2(1.0,0.0)), m.x),
               mix(dot(c,f - vec2(0.0, 1.0)), dot(d,f - vec2(1,1)), m.x), m.y);
}


float fbm(vec2 uv)
{

    //store the final value 
    float v = 0.;

    //store the current amplitude for the noise texture
    float a =.9;

    for(int i = 0; i < 6; i++)
    {
        v += a * noise(uv);
        a *= 0.5;
        uv *= 2.0;
    }

    return v;
}

vec2 rip(vec2 uv)
{
    return uv + fbm(uv + iTime);
}

