// Smoothnoise

vec2 SmoothNoise22( vec2 o ) 
{
	vec2 p = floor(o);
	vec2 f = fract(o);

	float n = p.x + p.y*57.0;

	vec2 a = hash21(n+  0.0);
	vec2 b = hash21(n+  1.0);
	vec2 c = hash21(n+ 57.0);
	vec2 d = hash21(n+ 58.0);

	vec2 f2 = f * f;
	vec2 f3 = f2 * f;

	vec2 t = 3.0 * f2 - 2.0 * f3;

	float u = t.x;
	float v = t.y;

	vec2 res = a + (b-a)*u +(c-a)*v + (a-b+d-c)*u*v;

    return res;
}


vec3 SmoothNoise32( vec2 o ) 
{
	vec2 p = floor(o);
	vec2 f = fract(o);

	float n = p.x + p.y*57.0;

	vec3 a = hash31(n+  0.0);
	vec3 b = hash31(n+  1.0);
	vec3 c = hash31(n+ 57.0);
	vec3 d = hash31(n+ 58.0);

	vec2 f2 = f * f;
	vec2 f3 = f2 * f;

	vec2 t = 3.0 * f2 - 2.0 * f3;

	float u = t.x;
	float v = t.y;

	vec3 res = a + (b-a)*u +(c-a)*v + (a-b+d-c)*u*v;

    return res;
}

