}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

/* linear step = poor smoothstep but faster */
float linstep(float a, float b, float x){
    return clamp((x-a)/(b-a),0.,1.);
}


float fbm(vec3 p){
    p*= .0035;
    vec2 uv = p.xz+FBM_OFFSET;
	float a = 1., Hz = 1., m = 1., h= p.y;					
    vec2 d = vec2(1.,0.);
    for(float i=1.;i<=STEP; i++){
        vec2 e = floor(uv*Hz), f = fract(uv*Hz);
        f = smoothstep(0.,1.,f);
        h += mix(
            	mix(H2(e),H2(e+d.xy),f.x)*a,
            	mix(H2(e+d.yx),H2(e+d.xx),f.x)*a,
            	f.y);

        a *= .2+.1*h+.05*i;
        //a *= .5;
        Hz *= 2.+.2*p.y;
        m += a;
    }
    h/=m;

    h *= h;

    return h;
}

