/*******************    buffer A    *********************/
/*														*/
/*               Shadows pre-calculation                */
/* The 4 channels are used to increase map resolution   */
/* 														*/
/********************************************************/


float ground(vec2 p){

    float a = .000001*(p.x*p.x + p.y*p.y);
    float h =smin(a*a,abs(1./a),.2)+.8/(50.*a+1.);
    float m = fbm(vec3(p.x,maxHill*h,p.y));
    return maxHill*(.4*h*m+.2*m+.4*h)+ texture(iChannel0,.00125*p.xy+.5).r;
}

vec3 getGndNormal(vec3 p){
    vec2 e = vec2(.1,0.);
	float dx = ground(p.xz+e.xy) - p.y;
    float dy = ground(p.xz+e.yx) - p.y;
    return normalize(vec3(dx,-e.x,dy));    
}


float traceGround(in vec3 pos, in vec3 ray){
    float t = 0.;
    float dh = pos.y-ground(pos.xz);
    if(dh<.1) return .0001;
    for(int i=0; i<75;i++){
        vec3 p = pos;
        t += .9*dh;
        p = pos+t*ray;
        if(p.y > 1.6*maxHill && ray.y>=0.){
            t = INFINI;
            break;
        }
        dh = p.y-ground(p.xz);
        if(dh<.1)break;
    }
    return t;
}


float shad(vec3 p){

        p.y = ground(p.xz);
        vec3 norm = getGndNormal(p);
        p -= .2*norm;
        float t = traceGround(p,LIGHT);
        if(t<INFINI) return .2;    
        else return 1.;    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float check = floor(texelFetch(iChannel1,ivec2(fragCoord),0).x -.1);	// checking if resolution has changed
    if(check == iResolution.x){												// in order to manage screen size change.
																			// if no change, just reading pre-calculated shadows
        fragColor = texture(iChannel1,fragCoord/iResolution.xy);			// stored in Buffer A
    }

    else{   
        vec2 st = 2000.*fragCoord.xy/iResolution.xy;				// if screen size has changed, bufferA size changed too 
																	// shadows must be recalculated.
        vec3 p = vec3(st.x,0.,st.y);
        fragColor.x = shad(p)+iResolution.x;						// storing new resolution info

        p.z = -p.z;
        fragColor.y = shad(p);

        p.x = -p.x;
        fragColor.z = shad(p);

        p.z = -p.z;
        fragColor.w = shad(p);
    } 
}

