const int marginL = 42;
const int marginR = 16;
const int marginT = 16;
const int marginB = 24;

int[] hex = int[] ( 432534, 287812, 431151, 492679, 630664, 989063, 923542, 1016866, 431766, 433799, 434073, 497559, 921886, 498071, 988959, 988945);

bool drawHex4(in int value, in vec2 coord, in vec2 pos)
{
    if (coord.x < pos.x || coord.y < pos.y) return false;
    ivec2 p = ivec2((coord - pos) / 2.);
    int dig = p.x / 5;
    p.x = p.x - dig * 5;
    if (dig >= 0 && dig < 4 && p.y < 5 && p.x < 4)
    {
        int v = hex[(value >> (12 - dig * 4)) & 15];
        return (v & (1 << (p.x + p.y * 4))) != 0;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float lineColor = 0.0;
    float textColor = 0.0;
    float graphColor = 0.0;
    float graphError = 0.0;

    ivec2 res = ivec2(iResolution.xy);
    ivec2 size = res - ivec2(marginL + marginR, marginT + marginB);
    ivec2 coord = (ivec2(fragCoord) - ivec2(marginL, marginB)) * ivec2(maxStep - minStep) / size + ivec2(minStep);

    // Nr of itermediate steps
    ivec2 stp = ivec2(maxStep - minStep) / size + 1;

    // Min distance between grid lines
    int sc = 32 << int(log(float(stp.x))/log(2.));

    bool fnd = false;

    if (coord.y >= minStep && coord.y <= maxStep && coord.x >= minStep && coord.x <= maxStep)
    {
        for (int sx = 0; sx < stp.x; sx++)
        {
            int vx = coord.x + sx;
            ivec2 tcoord = ivec2(vx % 1024, vx / 1024);
            vec3 rd = vec3(float(tcoord.x * 2 - 1023), 1024.0, float(tcoord.y * 2 - 1023));
            vec4 state = texture(iChannel0, rd);

            int vy = int(packHalf2x16(vec2(state.b, 0.0)));
            if (coord.y == vy)
            {
                fnd = true;
                graphColor = 1.0;
            } else
            if (vx != vy)
            {                
                graphError = 0.2;
            }
        }

        // Grid lines
    	if (coord.x >= minStep && coord.x <= maxStep)
    	{
            coord += stp/2;
    		if (coord.x % sc < stp.x) lineColor += 0.1;
    		if (coord.x % (sc << 2) < stp.x) lineColor += 0.2;
    		if (coord.x % (sc << 4) < stp.x) lineColor += 0.3;
    		if (coord.x % (sc << 6) < stp.x) lineColor += 0.4;
    		if (coord.y % sc < stp.y) lineColor += 0.1;
    		if (coord.y % (sc << 2) < stp.y) lineColor += 0.2;
    		if (coord.y % (sc << 4) < stp.y) lineColor += 0.3;
    		if (coord.y % (sc << 6) < stp.y) lineColor += 0.4;
        }

	}

    // Axis labels
    int addrStep = sc << 2;
    ivec2 textoffset = ivec2(20, 5) * (maxStep - minStep) / size;
    if (fragCoord.y < float(marginB))
    {
        int adr = ((coord.x + textoffset.x) / addrStep) * addrStep;
	    textColor += float(drawHex4(adr, fragCoord, vec2((adr - minStep - textoffset.x) * size.x / (maxStep - minStep) + marginL, 8)));
    }
    if (fragCoord.x < float(marginL))
    {
        int adr = ((coord.y + textoffset.y) / addrStep) * addrStep;
	    textColor += float(drawHex4(adr, fragCoord, vec2(0, (adr - minStep - textoffset.y) * size.y / (maxStep - minStep) + marginB)));
    }

    fragColor.rgb = vec3(1.0 - (lineColor + textColor + graphColor)) * vec3(1.0, 1.0 - graphError, 1.0 - graphError);
}

