// const int maxStep = 0x8100;

// - Zoom in on second range FC00..FFFF
// const int minStep = 0xFB00;
// const int maxStep = 0xFFFF;

// - Complete range to test
const int minStep = 0x0000;
const int maxStep = 0xFFFF;

