    vec2 st = (fragCoord.xy * scale);

    float thickness;
    float sharpness;

    if (iMouse.xy == vec2(0)) {
        thickness = 1.;
        sharpness = 2.;
    } else {
	    thickness = iMouse.y/iResolution.y * 30.0 * scale;
    	sharpness = iMouse.x/iResolution.x * scale;
    }


    float t = test(st, center, radius, thickness, sharpness);
    out_color = vec4( mix(white, black, t), 1.0);
}

