void sort(int idtemp, int R) //sort closest objects in N directions
{
    if(iscoincidence(idtemp, R)) return; //particle already sorted
    vec4 px = getParticlePos(idtemp);
    vec3 dx = px.xyz - obj.X;

    if(R < link0/4) //closest
    {
        int dir = int(inverseSF(dx, float(link0)).x); 
        int diri = dir%4;
        int dirR = dir/4;
        float nd = length(dx);
        if(dirR == R && nd < d[diri])
        {
            d[diri] = nd;
            lnk[diri] = float(idtemp);
        }
    } 
    else // far particles
    {
        int dir = int(inverseSF(dx, 4.).x); 
        int diri = dir%4;
        int rad = R;
        int dirR = rad;
        float nd = abs(length(dx) - exp2(float(rad))*avgR);
        if(dirR == R && nd < d[diri])
        {
            d[diri] = nd;
            lnk[diri] = float(idtemp);
        }
    }
}

void sortlinks(in links nb, int R)
{
    for(int i = min(0, iFrame); i<linkN; i++) sort(nb.ids[i], R);
}

void sortneighbors(int id, int R)
{
    links nb = getGraphLinks(id);
    sortlinks(nb, R);
}

void sortrandom(int id, int R)
{
    for(int i = min(0, iFrame); i<2; i++) sort((12*iFrame+ID+i)%tot_n, R);
}

