//prev closest particle buffer

float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

//intersection with the bisector plane
float rayBisectDist(vec3 p, vec3 d, vec3 a, vec3 b)
{
    vec3 n = b - a;
    float D = length(n);
    float cosnd = dot(n, d)/D; //ray plane normal angle cosine
    float l = D*.5 - dot(p - a, n)/D;
    float t = l/cosnd; //ray parameter
    return (cosnd>0.)?t:1e10;
}

//closest voronoi cell and ray intersection
vec4 findClosestIntersection(vec3 p, vec3 d, vec4 cobj, int mit)
{
    int id = int(cobj.w);
    if(id < 0)
    {
        id = 0;
        cobj.xyz = texel(ch0, i2xy(ivec3(id, 0, 0))).xyz;
    }

    int cid = id;
    vec3 cxy = cobj.xyz;
 	float cd = distance(p, cxy);

    for(int s = 0; s < mit; s++)
    {
        vec3 pxy = cxy;
        int pid = cid;
        //t = 1e10;
        for(int j = 0; j < linkN/4; j++)
   	    {
            vec4 nb = texel(ch0, i2xy(ivec3(pid, j, 1)));
            for(int i = 0; i < 4; i++)
            {
                if(nb[i]<0.) continue; 
                vec3 X = texel(ch0, i2xy(ivec3(nb[i], 0, 0))).xyz;
                float td = distance(p, X);
                if(td < cd) 
                {
                    cd = td;
                    cxy = X;
                    cid = int(nb[i]);
                }
            }
        }

        if(pid == cid) break; //found closest voronoi cell
    }

    return vec4(cxy,cid);
}

#define maxd 4.
#define mind 0.001
#define maxs 128

//implicit voronoi diagram ray marching
vec4 rayMarch(vec3 p, vec3 d, int cobjid)
{
    float td = 0.; 
    float ld = 0.; 
    float t = 0.;

    vec4 cobj = vec4(texel(ch0, i2xy(ivec3(cobjid, 0, 0))).xyz, cobjid);
    cobj = findClosestIntersection(p, d, cobj, 20);
    int n = 0;
    for(int i = 0; i < maxs; i++)
    {
        //march
        float DE = sdSphere(p + ld*d - cobj.xyz, 0.05);
        ld+=DE; td+=DE;

        //update position
        p += ld*d; ld = 0.; n++;
        //find new closest object
        cobj = findClosestIntersection(p, d, cobj, 4);

        //outide of the scene
        if(td > maxd) return vec4(p+ld*d, -1);
        //has hit the surface
        if(DE < mind*td) break;
    }
    return vec4(p + ld*d, cobj.w);
}

#define FOV 2.
mat3 getRot(vec2 a)
{

   mat3 theta_rot = mat3(1, 0, 0,
                         0, cos(a.y), sin(a.y),
                         0, -sin(a.y), cos(a.y)); 

   mat3 phi_rot = mat3(cos(a.x), sin(a.x), 0,
        		       -sin(a.x), cos(a.x), 0,
        		        0, 0, 1); 
   return transpose(phi_rot*theta_rot);
}

vec3 getRay(vec2 angles, vec2 pos)
{
    mat3 camera = getRot(angles);
    return normalize(transpose(camera)*vec3(FOV*pos.x, FOV*pos.y, 1.));
}

