// IQ's signed box formula.
float sBoxS(in vec3 p, in vec3 b, in float sf){

  vec3 d = abs(p) - b + sf;
  return min(max(max(d.x, d.y), d.z), 0.) + length(max(d, 0.)) - sf;
}
*/

