float bayer8(ivec2 uv)
{   
    uv %= 8;
	return texelFetch(iChannel1,uv,0).r;
}

// from https://necessarydisorder.wordpress.com/
float ease(float p, float g) {
    if(p < 0.){
        return 0.;
    } else if(p > 1.){
    	return 1.;
    } else {
        if (p < 0.5) 
            return 0.5 * pow(2.*p, g);
        else
            return 1. - 0.5 * pow(2.*(1. - p), g);
    }
}

float ease(float p, float power, float jump, float offs, float end) {
    float j = p;
    p *= offs;
    float r = (1.-cos(pi*p))/2.*jump;
    r = pow(r, power);
    r = mix(r, 1., pow(smoothstep(0.,1.,j),end));
	return r;
}

vec3 get(  vec2 uv, float t, inout float edge )
{

    float modD = 3.;

    t *= 1.;
    t = mod(t, 1.);
    vec3 col = vec3(0);

