////////////////////////////////////////////////////////////////////////////////
//
// Poncelet's Porism
// Matthew Arcus, 2020.
//
// Another gem of projective geometry. Given a pair of conics, if an
// n-gon can be inscribed in one such that it circumscribes the other,
// then there are an infinite number of such n-gons. It's difficult in
// general to find such an n-gon in the first place, but here's an
// easy special case.
//
// Inscribe a regular m/n-gon in a circle, find the incircle of the
// polygon and then apply a projective transformation. Here, we map
// points (-1,0),(0,1) and (0,-1) on the unit circle to themselves,
// and the point (1,0) to the mouse position.
//
////////////////////////////////////////////////////////////////////////////////

float N = 11.0, M = 5.0; // Polygon parameters

////////////////////////////////////////////////////////////////////////////////
//
// Geometric constructions
//
// Homogeneous coordinates used throughout (so the lines and points are
// "projective"):
// (x,y,z) represents the point (x/z,y/z) for z != 0
// (x,y,0) represents a "point at infinity" which acts like a direction.
// (a,b,c) represents the line ax + by + cz = 0
// (0,0,c) represents the "line at infinity"
//
////////////////////////////////////////////////////////////////////////////////

float PI = 3.14159;

// The line between two projective points, or the intersection of two
// projective lines, is just their cross product.
vec3 join(vec3 p, vec3 q) {
  return cross(p,q);
}

// Find a projective mapping taking p0,p1,p2,p4 to
// triangle of reference and unit point, ie:
// p0 -> (1,0,0), p1 -> (0,1,0), p2 -> (0,0,1), p3 -> (1,1,1)
// No three points collinear.
mat3 rproject(vec3 p0, vec3 p1, vec3 p2, vec3 p3) {
  // Just an inverse for the first three points
  // (the triangle of reference). No inverse if collinear.
  mat3 m = inverse(mat3(p0,p1,p2)); // column major!
  vec3 p3a = m*p3;
  // Then scale each row so the unit point (1,1,1) is correct
  m = transpose(m);
  // zero components here only if not collinear
  m[0] /= p3a[0];
  m[1] /= p3a[1];
  m[2] /= p3a[2];
  m = transpose(m);
  return m;
}

////////////////////////////////////////////////////////////////////////////////
// Distance functions
////////////////////////////////////////////////////////////////////////////////

float point(vec3 p, vec3 q) {
  float pz = p.z, qz = q.z;
  p *= qz; q *= pz;
  return distance(p,q)/abs(p.z);
}

float line(vec3 p, vec3 q) {
  return abs(dot(p,q)/(p.z*length(q.xy)));
}

float line(vec3 p, vec3 q, vec3 r) {
  return line(p,join(q,r));
}

float conic(vec3 p, mat3 m) {
  float s = dot(p,m*p);
  vec3 ds = 2.0*m*p; // Gradient
  return abs(s/(p.z*length(ds.xy))); // Normalize for Euclidean distance
}

mat3 circlemat(vec4 c) {
  // (x-a)^2 + (y-b)^2 = r^2
  // x2 -2ax + a2 + y2 -2by + b2 -r2 = 0
  vec3 q = c.xyz;
  float r2 = c.w;
  q /= q.z;
  return mat3(1,0,-q.x,
              0,1,-q.y,
              -q.x,-q.y,dot(q.xy,q.xy)-r2);
}

////////////////////////////////////////////////////////////////////////////////
// Drawing functions
////////////////////////////////////////////////////////////////////////////////

vec3 diagram(vec3 p, vec3 P) {
  vec3 A = vec3(0,-1,1), B = vec3(-1,0,1), C = vec3(0,1,1);
  mat3 X = rproject(A,B,C,P), Y = rproject(A,B,C,vec3(1,0,1));
  mat3 T = inverse(X)*Y, S = inverse(T);
  float theta = PI/N; // Segment half angle
  float lmin = 0.005, lmax = 0.008+fwidth(p.x);
  float pmin = 0.01, pmax = 0.02;
  float r = cos(M*theta);
  mat3 c1 = circlemat(vec4(0,0,1,1));
  mat3 c2 = circlemat(vec4(0,0,1,r*r));
  vec3 col = vec3(1,1,0.8);
  col = mix(vec3(0.5),col,0.75+0.75*smoothstep(lmin,lmax,conic(p,c1)));
  //col = mix(vec3(0,0,1),col,0.5+0.5*smoothstep(lmin,lmax,conic(p,c2)));
  col = mix(vec3(1,0,0),col,smoothstep(lmin,lmax,conic(p,transpose(S)*c1*S)));
  col = mix(vec3(0,0,1),col,smoothstep(lmin,lmax,conic(p,transpose(S)*c2*S)));
  float t = 0.2*iTime;
  for (float i = 0.0; i < N; i++) {
    vec3 p0 = vec3(cos(2.0*i*theta+t),sin(2.0*i*theta+t),1);
    vec3 p1 = vec3(cos(2.0*(i+M)*theta+t),sin(2.0*(i+M)*theta+t),1);
    //col = mix(vec3(0,0.5,0),col,0.5+0.5*smoothstep(lmin,lmax,line(p,p0,p1)));
    col = mix(vec3(0,1,0),col,0.5+0.5*smoothstep(lmin,lmax,line(p,T*p0,T*p1)));
  }
  for (float i = 0.0; i < N; i++) {
    vec3 p0 = vec3(cos(2.0*i*theta+t),sin(2.0*i*theta+t),1);
    //col = mix(vec3(0.5),col,smoothstep(pmin,pmax,point(p,p0)));
    col = mix(vec3(0),col,smoothstep(pmin,pmax,point(p,T*p0)));
  }
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  float scale = 1.2;
  vec3 p = vec3(scale*(2.0*fragCoord-iResolution.xy)/iResolution.y,1.0);
  vec3 P = iMouse.x <= 0.0 ? vec3(-0.5,0,1)
    : vec3(scale*(2.0*iMouse.xy-iResolution.xy)/iResolution.y,1);
  vec3 col = diagram(p,P);
  fragColor = vec4(pow(col,vec3(0.4545)),1);
}

#define PI 3.14159265
#define MAXIT 128
#define EPSILON 0.001

#define minx(a, b) ((a.w) < (b.w) ? (a) : (b))
#define maxx(a, b) ((a.w) > (b.w) ? (a) : (b))

float t;

float sphere(vec3 r, float a)
{
	return length(r) - a;
}

float bat(vec3 r, float a)
{
	r *= mat3(
		cos((r.x * sin(t * 20.) * 0.5) * 1.), -sin((r.x * sin(t * 20.) * 0.5) * 1.), 0., 
		sin((r.x * sin(t * 20.) * 0.5) * 1.), cos((r.x * sin(t * 20.) * 0.5) * 1.), 0.,
		0., 0., 1.
	);
	float d = (abs(r.x*r.y)*3. + 1./a) * (abs(sqrt(r.y+0.1) * r.y) + sin(r.y) + 1./a) * (abs(r.z) + 1./a);
	return length(r) - 1./ d;
}

float plane(vec3 r, vec3 o, vec3 n) {
	return dot(r - o, n);
}

float box(vec3 r, vec3 a)
{
	r.x += sin(r.y * PI) * 0.1;
	vec3 p = abs(r) - a * (sin(length(abs(abs(r)*4. - 2.) * 8.)) + 2.);
	return length(max(p, 0.)) + min(max(p.x, max(p.y, p.z)), 0.);
}

float shade(vec3 n, vec3 rd)
{
	return clamp(max(dot(n, -rd), 0.) + 1., 0., 1.);
}

vec3 fog(float z, vec3 col, vec3 fogCol)
{
	return mix(fogCol, col, exp(-z));
}

mat3 obj;

vec4 map(vec3 r)
{
	obj = mat3(
		cos(sin(-t + r.z * 0.2) * 0.31), -sin(sin(-t+r.z * 0.2) * 0.51), 0., 
		sin(sin(-t + r.z * 0.2) * 0.41), cos(sin(-t+r.z * 0.2) * 0.51), 0.,
		0., 0., 1.
	);

	float z = r.z;

	vec4 s0 = vec4(
		vec3(0.0, 0.8, 0.) * 0.8,
		bat(mat3(3.) * (obj * mod(r + 1. - vec3(cos(t*2. * 0.785 + z) * 0.5+ 0.5, sin(t*2. * 0.785 + z) * 0.25, t) + 0.45, 4.) - 1.), 1.3)
	);
	vec4 b1 = vec4(
		vec3(0.3, 0.1, 2.3) * 0.12,
		box((mod(r + 3.0, 4.) - 1.0), vec3(0.1, 4., 0.1))
	);

	return minx(s0, b1);
}

float hash(vec2 r) {
	return fract(sin(dot(r, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	t = iTime;

	vec2 uv = fragCoord.xy / iResolution.xy;
	uv -= 0.5;
	uv.x *= iResolution.x / iResolution.y;

	mat3 cam = mat3(
		cos(sin(t) * 0.1), -sin(sin(t) * 0.1), 0., 
		sin(sin(t) * 0.1), cos(sin(t) * 0.1), 0.,
		0., 0., 1.
	) * mat3(
		1., 0., 0.,
		0., cos(0.15), -sin(0.15), 
		0., sin(0.15), cos(0.15)
	);

	vec3 ro = vec3(0., - 0.5, - 1.0 + t);
	vec3 rd = cam * normalize(vec3(uv, -1.));
	vec3 r = ro;

	vec4 col = vec4(1.);
	vec3 bcol = vec3(1., 0.0, 0.2) + 0.0;

	vec4 c = vec4(bcol, 1.);

	float sh = 1.;

	float rs = 0.;

	for (int i = 0; i < MAXIT; ++i) {
		vec4 d = map(r);
		float z = length(r - ro);

		vec4 nc = minx(c, d);
		c.rgb = mix(nc.rgb, c.rgb, nc.w / c.w);
		c.w = nc.w;

		if (d.w < EPSILON) {
			col.rgb = d.rgb * shade(normalize(r), rd);
			col.rgb = d.rgb;
			col.rgb = fog(z * 0.1, col.rgb, bcol);
			break;
		}

		d.w *= 0.7 - 0.1 * hash(uv);
		r += rd * clamp(d.w, -0.5, 0.5) * 0.7;

		sh += (float(i) / float(MAXIT));
	}

	col.rgb *= exp(-sh * sh * 0.02);

	if (c.w > EPSILON) {
		col.rgb = fog(c.w, c.rgb, bcol * 0.1) * 0.2;
	}

	fragColor = vec4(col.rgb, 1.);
}

