float id;
vec3 glow;

// distance function
float map( vec3 p ) {
    // rotate the world
	p.xy *= rot(time*.1);
	p.xz *= rot(time*.2);

    // small point light on the center
	float d =  length(p);
	glow += vec3(1.) / ( .1 + d*200.);

    // 30 torus
	float s = .25;
	for(int i=0; i<30; i++) {
    	s += .25;
    	p.xy *= rot(time*.05);
    	p.xz *= rot(time*.1);

    	float d2 = torus(p,vec2(s, 0.14));

        // we accumulate the lighting here
    	float intensity = 1. / ( 1. + pow(abs(d2*att),1.3));
    	if(i == 6 && id == 0.) {
    		glow += vec3(1.,.3,1.) * intensity;
    	} else if(i == 15 && id == 1.) {
      		glow += vec3(1.,1.,.1) * intensity;
    	} else if(i == 20 && id == 2.) {
      		glow += vec3(.1,1.,.1) * intensity;
    	} else if(i == 25 && id == 3.) {
      		glow += vec3(.1,1.,1.) * intensity;
    	}

    	d = min(d, d2);
	}
	return d;
}



