         );

// Could use an ivec3 and count different types of flip
vec3 fold(vec3 pos, out int flips) {
  // We want to know the number of actual reflections made
  // so keep track of that in flips.
  flips = 0;
  for (int i = 0; i < NFOLDS; i++) {
    float k;
    k = dot(pos,A);
    if (k < 0.0) {
      flips += 1;
      pos -= 2.0*k*A;
    }
    k = dot(pos,B);
    if (k < 0.0) {
      flips += 1;
      pos -= 2.0*k*B;
    }
    k = dot(pos,C);
    if (k < 0.0) {
      flips += 1;
      pos -= 2.0*k*C;
    }
  }
  return pos;
}

// Perpendicular distance from p to segment ab
float segment(vec3 p, vec3 a, vec3 b) {
  vec3 pa = p - a;
  vec3 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

vec4 makeplane(vec3 p, vec3 q, vec3 r) {
  // dot(vec4(s,1),makeplane(p,q,r)) gives distance from plane
  vec3 n = cross(q-p,r-p);
  n = normalize(n);
  float k = -dot(p,n);
  // Fix so that away from origin is +ve distance
  // If k is 0, then plane through origin & the order of p,q,r
  // determine +ve direction (how?)
  if (k > 0.0) { k = -k; n = -n; }
  return vec4(n,k);
}

void mindistance(float d, int t, inout float dmin, inout int type) {
  if (d < dmin) { dmin = d; type = t; }
}
void maxdistance(float d, inout float dmax) {
  if (d > dmax) { dmax = d; }
}
void triangle(vec3 p0, vec3 p, vec3 q, vec3 r, float radius, int t, inout float dmin, inout int type) {
  mindistance(segment(p0,p,q)-radius,t,dmin,type);
  mindistance(segment(p0,q,r)-radius,t,dmin,type);
  mindistance(segment(p0,r,p)-radius,t,dmin,type);
}

float cradius = 0.1; // Sphere at origin
float pradius = 0.05; // Sphere at main vertex
float rradius = 0.03; // Sphere at other region vertices
float sradius = 0.01;

// The achiral scene.
float scene1(vec3 p, inout int type, inout float d) {
  vec3 a1 = a*dot(a,c);
  vec3 b1 = b*dot(b,c);
  vec3 c1 = c;
  vec3 O = vec3(0);

  // Always draw the dodecahedron vertices
    float k = 1.0 + pradius;
  mindistance(length(p-k*c) - pradius,0,d,type);
  if (true) {
    // Dodecahedron edges
    mindistance(segment(p,k*c,reflect(k*c,C))-0.03,1,d,type);
  }
  return d;
}

// The chiral scene. There is only one set of planes that can be split
// up into coherent chiral parts, that in fact form the compound of
// five tetrahedra.
float scene0(vec3 p, inout int type, inout float d) {
  float d1 = -1e8;
  maxdistance(dot(vec4(p,1),makeplane(vertices[0],vertices[14],vertices[10])),d1);
  maxdistance(dot(vec4(p,1),makeplane(vertices[0],vertices[12],vertices[10])),d1);
  if (d1 < d) { d = d1; type = 3; }
  return d;
}

float scene(vec3 p0, out vec2 uv, out int type) {
  type = 0;
  int flips = 0;
  float d = 1e8;
  vec3 p = fold(p0,flips);
  uv = p.xy;
  if (flips%2 == 0) {
    scene0(p,type,d);
  } else {
    scene0(reflect(p,A),type,d);
    scene0(reflect(p,B),type,d);
    scene0(reflect(p,C),type,d);
  }
  scene1(p,type,d);
  assert(type >= 0 && type <= 3);
  if (doclip && p0.x > d) {
    d = p0.x;
  }
  //if (type == 0 && distance(p0,c) < 0.2) type = 4;
  //return max(d,dot(p,b)-dot(b,c)); // Clip to dodecahedron
  return d;
}

void getcolor(int type, vec2 uv, out vec3 color, out float specularity) {
  specularity = 0.3;
  color = vec3(1,0,1);
  if (type == 0) { color = vec3(0.3,0.1,0.1); specularity = 0.0; }
  if (type == 1) color = vec3(0.8,0.8,0.5);
  if (type == 2) color = vec3(0.5,0.5,0);
  if (type == 3) { color = texture(iChannel2,uv).rgb; specularity = 0.0; }
}  

float map(vec3 p) {
  int type; vec2 uv;
  return scene(p,uv,type);
}

vec3 calcnormal(vec3 p) {
  const vec2 e = vec2(0.001, 0.0);
  vec3 n = vec3(map(p + e.xyy) - map(p - e.xyy),
                map(p + e.yxy) - map(p - e.yxy),
                map(p + e.yyx) - map(p - e.yyx));
  return normalize(n);
}

float march(in vec3 ro, in vec3 rd) {
  float maxd = length(ro) + 1.0;
  float precis = 0.001;
  float t = 0.0;
  float res = -1.0;
  for (int i = 0; i < 100; i++) {
    //assert(i < 20);
    float h = map(ro+rd*t);
    if (abs(h) < precis) return t;
    t += h;
    if (t < 0.0 || t > maxd) break;
  }
  return -1.0;
}

//-------------------------------------------------
//From https://www.shadertoy.com/view/XtXGRS#
vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (dorotate) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.2);
  }
  return p;
}

bool keypress(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

vec4 store(int i,int j) {
  return texelFetch(iChannel1, ivec2(i,j),0);
}

int keycount(int key) {
  return int(store(0,key).x);
}

const int CHAR_C = 67;
const int CHAR_N = 78;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;
const int KEY_UP = 38;
const int KEY_DOWN = 40;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  dorotate = !keypress(CHAR_R);
  doclip = keypress(CHAR_C);
  dosubtract = keypress(CHAR_S);
  bool dotexture = keypress(CHAR_T);
  bool calcnormals = !keypress(CHAR_N);
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  vec3 col = vec3(0.1 - 0.1*p.y);
  float camera = 2.5;
  camera *= 1.0 + 0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP));
  vec3 ro = vec3(0.0, 0.0, camera);
  vec3 rd = vec3(p, -2.0);
  vec3 light = normalize(vec3(0.5, 1.0, 3.0));
  ro = transform(ro);
  rd = transform(rd);
  light = transform(light);
  rd = normalize(rd);
  float t = march(ro, rd);
  if (t > 0.0) {
    vec3 pos = ro + t * rd;
    int type;
    vec2 uv;
    scene(pos,uv,type);
    vec3 n = calcnormal(pos);
    //assert(dot(n,rd) <= 0.0);
    float specularity = 0.0;
    getcolor(type,uv,col,specularity);
    float diffuse = clamp(dot(n, light), 0.0, 1.0);
    col *= 0.2+0.8*diffuse;
    float specular = pow(max(0.0,dot(reflect(light,n),rd)),50.0);
    col += specularity*specular*vec3(1);
  }
  col = pow(col, vec3(0.4545));
  if (alert) col.x = 1.0;
  fragColor = vec4(col, 1.0);
}

