const int nRecMax = 6;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, h;
  dMin = dstFar;
  q = p;
  q.y -= 0.35;
  d = PrRoundBoxDf (q, vec3 (1., 0.16, 0.7), 0.01);
  q = p - vec3 (0.7, 0.4, -0.72);
  d = max (d, - PrBoxDf (q, vec3 (0.17, 0.01, 0.2)));
  q.y -= -0.02;
  d = max (d, - PrBoxDf (q, vec3 (0.08, 0.08, 0.03)));
  q.y -= 0.01;
  d = max (d, - PrBoxDf (q, vec3 (0.045, 0.05, 0.05)));
  q = p;
  q.yz -= vec2 (0.3, -0.72);
  d = max (d, - PrBoxDf (q, vec3 (1.1, 0.01, 0.03)));
  q = p;
  q.x = abs (q.x) - 0.4;
  q.yz -= vec2 (0.35, -0.72);
  d = max (d, - PrBoxDf (q, vec3 (0.01, 0.17, 0.03)));
  q = p;
  q.x = abs (q.x) - 1.;
  q.yz -= vec2 (0.35, 0.05);
  d = max (d, - PrBoxDf (q, vec3 (0.02, 0.007, 0.7)));
  DMIN (1);
  q = p;
  q.x = abs (q.x) - 0.95;
  q.y -= 0.09;
  q.x -= 0.02 * q.y;
  d = PrRoundBoxDf (q, vec3 (0.025 + 0.02 * q.y, 0.09, 0.6), 0.005);
  q = p;
  q -= vec3 (-0.05, 0.09, 0.3);
  d = min (d, PrRoundBoxDf (q, vec3 (0.9, 0.09, 0.02), 0.005));
  DMIN (2);
  q = p;
  q.yz -= vec2 (0.55, 0.05);
  q.xz = abs (q.xz) - vec2 (0.55, 0.4);
  d = PrCylDf (q.xzy, 0.05, 0.05);
  DMIN (3);
  q = p;
  q.yz -= vec2 (0.71 + sSize.y, -0.45);
  d = 0.9 * max (PrRoundBoxDf (q, vec3 (sSize.x + 0.16, sSize.y + 0.12, 0.13), 0.03),
     - PrRoundBox2Df (q.xy, sSize + 0.01, 0.03));
  DMIN (4);
  q = p - vec3 (0.7, 0.4, -0.55);
  d = PrBoxDf (q, vec3 (0.16, 0.008, 0.16));
  DMIN (5);
  q = p;
  h = sSize.y + 0.05 - 0.1 * (q.z - 0.05);
  q.yz -= vec2 (0.6 + h, 0.05);
  d = 0.9 * PrRoundBoxDf (q, vec3 (sSize.x + 0.12, h, 0.6), 0.03);
  DMIN (6);
  q = p;
  q.x = mod (q.x + 1., 2.) - 1.;
  q -= bPos;
  d = PrSphDf (q, 0.2);
  DMIN (11);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjAO (vec3 ro, vec3 rd)
{
  float ao, d;
  ao = 0.;
  for (int j = VAR_ZERO; j < 8; j ++) {
    d = float (j + 1) / 16.;
    ao += max (0., d - 3. * ObjDf (ro + d * rd));
  }
  return 0.4 + 0.6 * clamp (1. - 0.2 * ao, 0., 1.);
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col;
  vec2 q;
  float d;
  if (rd.y >= 0.) col = vec3 (0.1, 0.2, 0.3) + 0.2 * pow (1. - rd.y, 5.);
  else {
    d = - ro.y / rd.y;
    q = ro.xz + d * rd.xz;
    col = mix (mix (vec3 (0.3, 0.1, 0.), vec3 (0.4, 0.2, 0.), smoothstep (0.4, 0.6, Fbm2 (q * vec2 (1., 4.)))),
       vec3 (0.3, 0.4, 0.5), 1. - min (1., exp2 (2. - 8. * d / dstFar)));
  }
  return col;
}

vec3 ShowScene (vec3 ro)
{
  vec4 col4;
  vec3 col, rd, roh, vn, q;
  vec2 sb, a;
  float dstObj, ao;
  bool isBg, isScrn;
  isBg = false;
  isScrn = true;
  for (int k = VAR_ZERO; k <= nRecMax; k ++) {
    rd = normalize (vec3 (ro.xy, 7.));
    if (k == 0) ro.xy = vec2 (0.);
    ro.z -= 20. * sSize.y;
    rd = vuMat * rd;
    ro = vuMat * ro;
    ro.y += sMidHt;
    dstObj = ObjRay (ro, rd);
    if (dstObj >= dstFar) isBg = true;
    else {
      ro += dstObj * rd;
      roh = ro;
      if (idObj != 6) isScrn = false;
      else {
        ro.y -= sMidHt;
        sb = abs (ro.xy) - sSize;
#if GURU
        isScrn = (max (sb.x, sb.y) < 0. && ro.z < -0.5 && k < nRec);
#else
        isScrn = (max (sb.x, sb.y) < 0. && ro.z < -0.5);
#endif
      } 
    }
#if GURU
    if (! isScrn || isBg || k == nRec) break;
#else
    if (! isScrn || isBg) break;
#endif
  }
  if (isBg) {
    col = BgCol (ro, rd);
    if (rd.y < 0.) {
      roh = ro - (ro.y / rd.y) * rd;
      if (length (roh.xz) < 2.) col *= ObjAO (roh, vec3 (0., 1., 0.));
    }
  } else {
    col4 = vec4 (0.8, 0.81, 0.79, 0.);
    PxInit ();
    if (idObj < 11) {
      if (idObj == 1) {
        if (ro.z < -0.65) {
          col4.rgb *= 1.2;
          sb = ro.xy - vec2 (-0.82, 0.42);
          sb.x -= 0.2 * sb.y;
          if (PxText (sb * 260., 1) > 0.) col4 = vec4 (0.1);
          else if (PxText ((ro.xy - vec2 (-0.75, 0.21)) * 240., 2) > 0.)
             col4 = vec4 (0.1, 0.1, 0.8, 0.3);
          else if (abs (abs (ro.x) - 0.9) < 0.02 && abs (ro.y - 0.3) < 0.008)
             col4 = (ro.x < 0.) ? vec4 (0., 1., 0., -1.) : ((mod (0.33 * tCur, 1.) < 0.3) ?
             vec4 (1., 0., 0., -1.) : vec4 (0.4, 0.4, 0.4, 0.1));
        }
      } else if (idObj == 2) {
        col4 *= 0.9;
      } else if (idObj == 3) {
        col4.rgb = vec3 (0.4, 0.3, 0.1);
      } else if (idObj == 4) {
        if (ro.z < -0.45) col4 *= 1.2;
        if (ro.z < -0.6 && abs (ro.x) < sSize.x + 0.15 &&
           abs (ro.y - sMidHt) < sSize.y + 0.11) col4 *= 0.8;
      } else if (idObj == 5) {
        col4.rgb = vec3 (0.1);
      } else if (idObj == 6) {
        if (length (ro.yz - vec2 (-0.4, -0.05)) < 0.15 &&
           mod (ro.y + 0.4 - 0.01, 0.04) < 0.02) col4 *= 0.4;
        else if (ro.z < -0.5) {
          col4 = vec4 (0.1);
#if GURU
          sb = (ro.xy - vec2 (0., 0.6 * sSize.y)) * 340.;
          if (PxText (sb, 0) > 0.) col4 = vec4 (1., 0., 0., -1.);
          else if (mod (tCur, 1.) < 0.5) {
            ro.xy = (abs (sb - vec2 (0., -1.5) * cSpace) - vec2 (29., 7.) * cSpace);
            if (max (ro.x, ro.y) < 1.25 &&
               (min (abs (ro.x), abs (ro.y)) < 1.25)) col4 = vec4 (1., 0., 0., -1.);
          }
#endif
        }
      }
    }
    if (! isScrn) {
      if (col4.a >= 0.) {
        vn = ObjNf (roh);
        if (idObj < 11) vn = VaryNf (128. * roh, vn, 0.2);
        else {
          sb = Rot2D (vn.xy, -0.1 * pi);
          a = sign (mod (floor (8. * vec2 (atan (sb.x, vn.z) + 0.6 * pi * tCur, asin (sb.y)) / pi), 2.) - 0.5);
          col4 = mix (vec4 (1., 0., 0., 0.2), vec4 (0.95, 0.95, 1., 0.2), step (0., a.x * a.y));
        }
        ao = ObjAO (roh, vn);
        col = ao * (col4.rgb * (0.2 + 0.1 * max (0., - dot (vn, normalize (vec3 (ltDir.xz, 0.).xzy))) +
           0.8 * (max (0., dot (vn, ltDir)))) + col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.));
      } else col = col4.rgb;
    }
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ut;
  float el, az, zmFac, sr, ds, s, t;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  ut = abs (uv) - vec2 (1.33, 1.);
  if (max (ut.x, ut.y) > 0.003) col = vec3 (0.82);
  else {
    dstFar = 60.;
    ltDir = normalize (vec3 (1., 3., -1.));
    ds = SmoothBump (0.2, 0.8, 0.1, mod (0.043 * tCur, 1.));
    az = -0.07 * pi * (2. * floor (mod (0.043 * tCur + 0.5, 2.)) - 1.) * (1. - ds);
    el = -0.01 * pi * (1. - ds);
    if (mPtr.z > 0.) {
      az += pi * mPtr.x;
      el += 0.3 * pi * mPtr.y;
    }
    az = clamp (az, -0.4 * pi, 0.4 * pi);
    vuMat = StdVuMat (clamp (el, -0.4 * pi, 0.01 * pi), az);
    sSize = 0.55 * vec2 (1.33, 1.);
    sMidHt = 1.26;
    nRec = int ((1. - abs (2. * mod (0.1 * tCur, 1.) - 1.)) * float (nRecMax + 1));
    zmFac = 12. * sSize.y * ds;
    t = mod (0.3 * tCur, 2.);
    s = sign (2. * floor (t) - 1.);
    t = mod (t, 1.);
    bPos = vec3 (1.5 * s * (0.5 - t), 0.2 + 8. * t * (1. - t), -5.);
#if ! AA
    const float naa = 1.;
#else
    const float naa = 3.;
#endif  
    col = vec3 (0.);
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = float (VAR_ZERO); a < naa; a ++) {
      col += (1. / naa) * ShowScene (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
         sr * (0.667 * a + 0.5) * pi), zmFac));
    }
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

