vec2 sqr(vec2 x)
{
    return vec2(x.x*x.x - x.y*x.y, 2.*x.x*x.y); 
}

float mandelbrot(vec2 x)
{
    vec2 z = vec2(0), dz = vec2(0);
    for(int i = 0; i < 128; i++)
    {
        dz = 2.*vec2(dot(z, dz*a.yz), dot(z, dz.yx)) + a.yx;
        z = sqr(z) + x;
        if(length(z)>32.) break;
    }
    return 0.5*sqrt(dot(z,z)/dot(dz,dz))*log(dot(z,z));
}

