#define MAX_DIST 1000.0
#define MAX_STEPS 1000
#define EPSILON 0.01
#define STEP_RATIO 0.75


#define NORMAL_DELTA 0.001

vec2 measure(vec3 point) {
    vec3 rv = vec3(mod(point.x+5.0, 10.0)-5.0, 
                  point.y, 
                  point.z);

    float r = max(length(rv.xy) - 0.75,
    	max(point.z,-point.x-0.5));
	vec2 closest = vec2(r, 1.0);

    vec3 gv = vec3(point.x,
        mod(point.y+5.0, 10.0)-5.0, 
        point.z);
    float g = max(length(gv.yz) - 0.50,
	    max(-point.x+0.5, -point.y-5.5));
    if (g < closest.x) {
        closest = vec2(g, 2.0);
    }
    // yellow
    vec3 bv = vec3(point.x,
        point.y,
        mod(point.z+5.0, 10.0)-5.0);
    float b = max(length(bv.zx) - 0.75,
    	max(point.z-5.5, -point.y+0.5));
    if (b < closest.x) {
        closest = vec2(b, 3.0);
    }

    return closest;
}

vec3 calc_surface_normal(vec3 hit) {
	return normalize(vec3(
            measure(hit+vec3(NORMAL_DELTA, 0.0, 0.0)).x - measure(hit-vec3(NORMAL_DELTA, 0.0, 0.0)).x,
            measure(hit+vec3(0.0, NORMAL_DELTA, 0.0)).x - measure(hit-vec3(0.0, NORMAL_DELTA, 0.0)).x,
            measure(hit+vec3(0.0, 0.0, NORMAL_DELTA)).x - measure(hit-vec3(0.0, 0.0, NORMAL_DELTA)).x
    ));
}

vec4 aaTrace(in vec2 fragCoord )
{
    // apply smooth sawtooth time distortion for eye
    // this makes the eye move very slow, then fast, over and over
    float eyeTime = iTime*2.0/3.1415926536 + sin(iTime) 
        +sin(iTime*2.0) / 4.0
        +sin(iTime*3.0) / 9.0
        +sin(iTime*4.0) / 16.0
        +sin(iTime*5.0) / 25.0;
    vec3 eye = vec3(
        sin(eyeTime*0.11)* 150.0 + 300.0,
        sin(eyeTime*0.05)* 150.0 + 300.0,
        cos(eyeTime*0.13)* 150.0 - 300.0
    );
    // minor time distortion for whatever we're looking at
    float lookTime = 4.0*(iTime + sin(iTime+2.0));
    // keep us looking more or less at the back corner but
    // move your around a bit
    vec3 look_at = vec3(
        sin(lookTime*0.0730)*50.0+50.0,
        sin(lookTime*0.0101)*20.0+30.0,
        cos(lookTime*0.0710)*50.0+50.0
    );
    // let's do the time warp again
    float upTime = iTime + sin(iTime*1.7+1.0);
    // get a bit of roll going by adjusting the up vector too
    vec3 up = normalize(vec3(
        sin(upTime*0.0610)*1.0,
        sin(upTime*0.0430)*1.0+3.0,
        cos(upTime*0.0310)*1.0
    ));
    vec3 ray = calculateRay(iResolution, fragCoord, eye, look_at, up);

    vec3 where = eye;
    float total_dist = 0.0;
    vec2 current;
    int who = 0;
    float current_dist = 0.0;
    float best_dist = MAX_DIST;
    // main raymarching loop
    for(int steps =0;steps < MAX_STEPS; steps++) {
        // lazy bailout if we go past the back walls
        if (where.y < -1.0 || where.x < -1.0 || where.z > 1.0) {
            break;
        }
        current = measure(where);
        current_dist = current.x;
        if (current_dist < EPSILON) {
            who = int(current.y);
            break;
        }
        if (current_dist < best_dist) {
            best_dist = current_dist;
        }
        total_dist += current_dist * STEP_RATIO;
        if (total_dist > MAX_DIST) {
            break;
        }
        where = eye + total_dist * ray;
    }
    if (who == 1){
        float glow = clamp(15.0 - best_dist, 0.0, 15.0) / 15.0;
        vec3 pink = vec3(1.0, 0.6, 0.5);
        return vec4(glow * pink, 1.0);
    }
    if (who == 2){
        float glow = clamp(1.0 - best_dist, 0.0, 1.0);
        vec3 green = vec3(0.5, 1.0, 0.5);
        return vec4(glow * green, 1.0);
    }
    if (who == 3){
        float glow = clamp(1.0 - best_dist, 0.0, 1.0);
        vec3 cc = vec3(0.8, 0.8, 0.2);
        return vec4(glow * cc, 1.0);
    }
    return vec4(0);
}

#define AA_JITTER 0.1
#define AA_SPREAD 1.0
#define AA_DEPTH  4
#define AA_DEPTH_F 4.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    for (int aax = 0; aax < AA_DEPTH; aax++) {
	    for (int aay = 0; aay < AA_DEPTH; aay++) {
            vec2 dp = AA_SPREAD * vec2(float(aax), float(aay)) / AA_DEPTH_F;
            vec2 subpixel = dp + fragCoord;
            ivec2 ip = ivec2(int(subpixel.x * AA_DEPTH_F), int(subpixel.y*AA_DEPTH_F));
            vec2 jitter = vec2(
                float(((2621 * ip.x + ip.y) * 1709) &255) / 256.0 - 0.5,
            	float(((1999 * ip.x + ip.y) * 3119) &255) / 256.0 - 0.5
            ) * AA_JITTER;
            fragColor += aaTrace(subpixel + jitter);
        }
    }
    fragColor /= 16.0;
}

