float sphere(vec3 r, float a)
{
	return length(r) - a;
}

float pointy(vec3 r, float a)
{
	float d = (abs(r.x*3.) + 1./a) * (abs(r.y) + 1./a) * (abs(r.z) + 1./a);
	return length(r) - 1./ d;
}

float plane(vec3 r, vec3 o, vec3 n) {
