vec4 geo(vec3 p)
{
	vec3 color_head = mix(vec3(0.0,1.0,0.0), vec3(1.0,1.0,0.0), cos(iTime * 11.));

	float height = -0.1;

	vec4 head = vec4(color_head,length(p - vec3(0.75,height,1.5))-0.4) 
        + nnn(p.x,4.2,10.5,0.2);
	vec4 eye = vec4(vec3(1.0),length(p - vec3(0.75,height+0.15,1.3))-0.25) 
        + nnn(p.x, 4.2, 10.5, 0.2);
	vec4 eyeB = vec4(vec3(0.),length(p - vec3(0.6,height+0.18,1.28))-0.15) 
        + clamp(nnn(p.x, 4.2, 10.5, 0.2),-0.9,0.15);
	vec4 plane = vec4(0.2,0.6,0.4,(p.y + 0.55));
	vec3 bRot = p;
	bRot.yz *= rot(0.4);
	vec4 box = vec4(1.0,1.0,0.0,length(
        max(abs(bRot - vec3(-0.9,-0.6,1.3)) - vec3(1.15,0.02,0.02),0.0) + nnn(p.x, 2.1, 10.5, 0.2)) - 0.06);

	vec4 g = opu(head, plane);
	vec4 g2 = opu(g, box);
	vec4 g3 = opu(g2, eye);
	vec4 g4 = opu(g3, eyeB);
	return g4;
