vec4 march(ray r)
{
	vec3 col = vec3(1.0);
	for(int i = 0; i < 64; i++)
	{
		vec3 p = r.o + r.d * r.l;
		vec4 g = geo(p);
		r.l += g.w;
		col = g.rgb;
		if(r.l > 32.)
			break;
	}
	return vec4(col, r.l);
}

vec3 normal(vec3 p)
{
	vec2 of = vec2(0.001, 0.0);
	float copy = geo(p).w;
	return normalize(copy - vec3(geo(p - of.xyy).w, geo(p - of.yxy).w, geo(p - of.yyx).w));
}

float shadow(vec3 ro, vec3 rd, float b)
{
	float t = 0.01;
	float res = 1.;
	for(int i = 0; i < 16; i++)
	{
		float copy = geo(ro + rd * t).w;
		res = min(res, b * copy/t);
		if(copy < 0.001)
			break;	
		t += copy;
		if(t < 0.01 || t > 3.)
			break;
	}
	return clamp(res,0.2,1.);
}

