);

bool IsInTime(const float duration, const float playAtTime, const float time, out float relativeTime)
{
    relativeTime = time - playAtTime;
    return ((time >= playAtTime) && ((time - duration) < playAtTime));
}

vec2 ProcessLeftRight(const float leftRight)
{
    return vec2((leftRight > 0.5) ? cos((leftRight - 0.5) * pi) : 1.0, (leftRight < 0.5) ? cos((0.5 - leftRight) * pi) : 1.0);
}

float ProcessFadeIn(const float fadeInTime, const float time)
{
    return (((time < fadeInTime) && (fadeInTime > 0.0)) ? (1.0 - cos((time * halfPi) / fadeInTime)) : 1.0);
}

float ProcessFalloff(const float falloffTime, const float time)
{
    return (((falloffTime > 0.0) && time >= 0.0) ? exp((-time * doublePi) / falloffTime) : 1.0);
}

float SquareWave(const float frequency, const float highRatio, const float time)
{
    float beats_per_second = 1.0 / frequency;
    float normalized_square_time = mod(time, beats_per_second) / beats_per_second;
    return ((normalized_square_time > highRatio) ? -1.0 : 1.0);
}

float TriangleWave(const float frequency, const float triangleAlignment, const float time)
{
    float ret = 1.0;
    float beats_per_second = 1.0 / frequency;
    float normalized_triangle_time = mod(time, beats_per_second) / beats_per_second;
    if (normalized_triangle_time < triangleAlignment)
    {
        ret = (clamp(normalized_triangle_time / triangleAlignment, 0.0, 1.0) * 2.0) - 1.0;
    }
    else if (normalized_triangle_time > triangleAlignment)
    {
        ret = 1.0 - (clamp((normalized_triangle_time - triangleAlignment) / (1.0 - triangleAlignment), 0.0, 1.0) * 2.0);
    }
    return ret;
}

float SineWave(const float frequency, const float time)
{
    return sin(time * doublePi * frequency);
}

float WhiteNoiseWave(const float time)
{
    return (fract(sin(mod(time, 1.0) * 12865.9898) * 43758.5453) * 2.0) - 1.0;
}

vec2 ProcessAudioEffects(const uint audioEffectsIndex, const float time)
{
    AudioEffects audio_effects = audioEffectsArray[audioEffectsIndex];
    return ProcessLeftRight(audio_effects.leftRight) * (audio_effects.volume * ProcessFadeIn(audio_effects.fadeInTime, time) * ProcessFalloff(audio_effects.falloffTime, time - audio_effects.fadeInTime - audio_effects.duration));
}

float GetDuration(const uint audioEffectsIndex)
{
    AudioEffects audio_effects = audioEffectsArray[audioEffectsIndex];
    return audio_effects.fadeInTime + audio_effects.duration + audio_effects.falloffTime;
}

vec2 PlayNotes(const float time)
{
    vec2 ret = vec2(0.0);
    SquareNote square_note;
    TriangleNote triangle_note;
    SineNote sine_note;
    WhiteNoiseNote white_noise_note;
    float duration;
    float relative_time;
    int note_index;
    for (note_index = 0; note_index < squareNotes.length(); note_index++)
    {
		square_note = squareNotes[note_index];
        duration = GetDuration(square_note.audioEffectsIndex);
        if (IsInTime(duration, square_note.playAtTime, time, relative_time))
        {
            ret += SquareWave(square_note.frequency, square_note.highRatio, time) * ProcessAudioEffects(square_note.audioEffectsIndex, relative_time);
        }
    }
    for (note_index = 0; note_index < triangleNotes.length(); note_index++)
    {
		triangle_note = triangleNotes[note_index];
        duration = GetDuration(triangle_note.audioEffectsIndex);
        if (IsInTime(duration, triangle_note.playAtTime, time, relative_time))
        {
            ret += TriangleWave(triangle_note.frequency, triangle_note.triangleOffset, time) * ProcessAudioEffects(triangle_note.audioEffectsIndex, relative_time);
        }
    }
    for (note_index = 0; note_index < sineNotes.length(); note_index++)
    {
		sine_note = sineNotes[note_index];
        duration = GetDuration(sine_note.audioEffectsIndex);
        if (IsInTime(duration, sine_note.playAtTime, time, relative_time))
        {
            ret += SineWave(sine_note.frequency, time) * ProcessAudioEffects(sine_note.audioEffectsIndex, relative_time);
        }
    }
    for (note_index = 0; note_index < whiteNoiseNotes.length(); note_index++)
    {
		white_noise_note = whiteNoiseNotes[note_index];
        duration = GetDuration(white_noise_note.audioEffectsIndex);
        if (IsInTime(duration, white_noise_note.playAtTime, time, relative_time))
        {
            ret += WhiteNoiseWave(time) * ProcessAudioEffects(white_noise_note.audioEffectsIndex, relative_time);
        }
    }
    return ret;
}

vec2 mainSound(in int samp, float time)
{
    if (time >= musicPlayTime)
    {
        return vec2(0.0);
    }
    vec2 volume = vec2(0.0);
    float music_time = mod(time, repeatMusicTime);
    for (uint anti_aliasing_index = 0U; anti_aliasing_index != antiAliasing; anti_aliasing_index++)
    {
        volume += PlayNotes(music_time - (float(anti_aliasing_index) / (iSampleRate * float(antiAliasing))));
    }
    return volume / float(antiAliasing);
}

