    nn *= 0.01*sin(t);
    float m = 1.;
    float width = 15.;
    s += sin( note(chords[id][1], 3.) *tt + nn.x*1.);
    s += sin( note(chords[id][1], 3.) *tt + nn.x*1.);
    float oct = 3.;  
    for(float i = 0.; i < 5. ; i++){
        float a = (1. + i/1.);
    	//s += sin( note(chords[id].x, 3.) *tt + nn.x)*m;
    	s.x += sin( note(chords[id].x, oct) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].y, oct) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].z, oct) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].w, oct) *tt*a + nn.x)*m;
    	s.y += sin( note(chords[id].x, oct) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].y, oct) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].z, oct) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].w, oct) *tt*a + nn.y + width)*m;
    	m *= 0.75;
    }
    s *= env;
    if(gT < msr*4.)
    	bitreduction(s, 0.4);

    s *= 0.04;
    //s = sin(s*3.);
    //s /= 2.;
    //s = sin(s);
	ret;
}


vec2 hats(float t){
	init;

    float r = hbeat;
    t = mod(t + 0., r);

    float o = offsH[int(mod(floor(t/hbeat), 7.))]*r;
    t -= o;


    sampleratereduction(t, 1./iSampleRate);
    vec2 nn = N[0] + N[1]  + N[2] + N[3]  ;

    float amt = 0.1;
    float env = exp(-t*7.4);
    //float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((3000. )*tt)*exp(-t*2.)*0.5 + ( 2000. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    float f =  sin((11000. )*tt)*exp(-t*0.4)*0.215 +sin((3000. )*tt)*exp(-t*2.)*0.5 + ( 3000. + exp(-t*60.)*100.)*tt +lpnoise(t, 2500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    bitreduction(s, 0.7);
    s *= 1.;
    s *= min(floor(t) + 1., 1.);
    ret;
}
vec2 hatsb (float t){
	init;

    t = mod(t + 0., hbeat/2.);


    vec2 nn = N[0] + N[4]*0.87  + N[2]*0.6 + N[3]*0.5  ;

    float amt = 0.5;
    float env = exp(-t*10.);
    float f =  sin(
        (7000. )*tt)*exp(-t*0.4)*0.215 
        +sin((4000. )*tt)*exp(-t*2.)*0.5 
        + ( 1250. + exp(-t*60.)*100.)*tt +N[6].x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;


    s *= 0.08;
    ret;
}

vec2 crash(float t){
	init;

    t = mod(t + 0., msr/2.);

    vec2 nn = N[1];

    float amt = 2.;
    float env = exp(-t*0.2);
    float f =  sin(
        (2000. )*tt)*exp(-t*0.4)*0.215 
        +sin((4000. )*tt)*exp(-t*2.)*0.5 
        + ( 1250. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;


    s *= 0.9;
    bitreduction(s, 0.9);

    s *= 0.4;
    ret;
}

vec2 cReverb(float t){
	init;

    //sampleratereduction(t, (8.2*1./iSampleRate));


    float iters = 100.;
    float size = 150.;

    if(gT > msr *8.)
        iters *= 4.;

    for(float i = 0.; i < iters ; i++ ){
        float att = smoothstep(1.,0.9,i/iters);
    	//s += lead(t - size*i/iSampleRate + 10000.2*N[int(mod(i,7.))].x/iSampleRate )*(pow(sc,0.9))*att;
        float m = size*i/iSampleRate + noise(i).x*100./iSampleRate;
        s += lead(t + m )*(pow(sc,0.9))*att*0.05;
        s += leadb(t + m);
        s += hats(t + m)*(0.2 + pow(sc,1.4))*0.001;
    }
    s /= iters;
    s *= 3.;

    if (gT > msr*2.)
    	s = mix(s,s * (pow(sc,0.9)), 0.5);
    //bitreduction(s, 0.0001);

    ret;
}


void initKickPatt(float t){

    float r = beat/4.;

    float id = floor(t/r);


    for(int i = 0; i < 16; i++){

        vec4 r = r11(id + float(i));

    	pattK[i] = r.x < probK[i];

    }
}


void initNoise(float t){

    N[0] = lpnoise(t, 400.);
    N[1] = lpnoise(t, 500.);
    N[2] = lpnoise(t, 700.);
    N[3] = lpnoise(t, 1000.);
    N[4] = lpnoise(t, 1400.);
    N[5] = lpnoise(t, 1900.);
    N[6] = lpnoise(t, 2500.);
    N[7] = lpnoise(t, 5500.); 

}
vec2 mainSound( in int samp, float t )
{
    t += OFFS;
    gT = t;
    //t += msr*4.;
    vec2 s= vec2(0);

    initKickPatt(t);
    initNoise(t);


    vec2 k = kick(t,sc)*1.;
    vec2 sn = snare(t);

    vec2 ha = hats(t)*(0.2 + pow(sc,1.4))*0.01;
    vec2 hb = hatsb(t)*0.7;

    vec2 drums = k + sn*1. + ha*3. ;
    //drums *= 0.5;
    drums = sin(drums*1.2);
    //drums = smoothify(drums, 0.2);
    //vec2 hb= hatsb(t)*(pow(sc,4.4))*0.1;
    vec2 bass = bass(t)*(pow(sc,1.))*0.3;
    vec2 c = chord(t)*(pow(sc,1.))*0.4;
    vec2 cguit = cguit(t)*(pow(sc,1.))*0.5;
    //vec2 l = lead(t)*(pow(sc,0.9))*0.4;
    //vec2 lb = leadb(t)*(pow(sc,0.9))*0.4;
    //vec2 lc = leadc(t)*(pow(sc,0.9))*0.4;
	//vec2 cr = crash(t);

    vec2 rev = cReverb(t);
    vec2 lb = leadb(t)*(pow(sc,0.9))*1.5;
    vec2 cr = crash(t)*(pow(sc,0.9))*0.1;

    if(t > msr*8.){
    	t = mod(t,msr*2.*4.) + msr*8.;
    }

    if(t < msr*2.){
    	s += rev*4.;
    	s += lb;
        if(t > msr*1.){
    		s += cr;
        }
    } else if(t < msr*4.) {
    	s += lb;
		s += hb;
        s += c;
        if(t > msr*3.){
            s += k;
        	s += cguit;
            s -= c;
        	s += rev*3.;
        }
    }else if(t < msr*6.) {
    	s += drums;
        s += bass;
		s += rev*3.;
        if(t > msr*5.){
        	s += hb;
        }
    }else if(t < msr*8.) {
        s += cr;
    	s += drums;
        s += bass;
		s += rev*3.;
        s += cguit*1.7;
    }else if(t < msr*10.) {
        s += cr;
    	s += drums;
        s += bass;
        s += lb;
		s += rev*3.;
        s += cguit;
    }else if(t < msr*12.) {
        s += cr;
        s += c;
    	s += drums;
        s += bass;
        s += lb;
		//s += rev*3.;
        s += cguit;
    }


    s *= 0.9;

    s = smoothify(s, 0.3);
    s = smoothify(s, 0.1);

    s = clampify(s, 1.7);


    s *= 0.5;

    return s;
}

