////////////////////////////////////////////////////////////////////////////////
//
// Excavated Dodecahedra
//
// Matthew Arcus, 2020
//
// Take an icosahedral kaleidoscope (using precalculated values), and
// take slices through the fundamental region with planes through the
// vertices of a dodecahedron. The result is a great range of shapes -
// stellations, facetings and other more irregular but pretty shapes.
//
// <mouse>: rotation
// <up>/<down>: move in/out
// c: clip at x=0
// n: compare numerical normal with exact
// r: autorotation
// s: subtract one of the planes (or the half space it bounds)
// t: apply texturing
// 
// Each plane (we combine two) goes through he main vertex of the
// fundamental region, and cuts off a half space and the two half
// spaces are intersected (as well as being clipped to the
// dodecahedron). The display sequences through all 'interesting'
// combinations.

// Pressing 's' subtracts one of the spaces rather than doing an intersection,
// which gives interesting results, but sometimes we end up with no
// polyhedron at all.
//
// Experimenting with exact normals - compare with the usual numerical
// procedure with 'n' - still problems where the ray has hit a
// backface and we get the wrong normal.
//
////////////////////////////////////////////////////////////////////////////////

bool dorotate = true;
bool doclip = false;
bool dosubtract = false;

const float PI	= 3.14159265359;
int NFOLDS = 5;

bool alert = false;
void assert(bool test) {
  if (!test) alert = true;
}

// Face plane normals, dot(A,B) = -cos(PI/P) etc.
vec3 A = vec3(1,0,0);
vec3 B = vec3(-0.5,0.866025,0);
vec3 C = vec3(-0.809017,-0.467086,0.356822);

// Unit length vertices of fundamental trihedron
vec3 a = vec3(0.309017,0.178411,0.934172);
vec3 b = vec3(0,0.607062,0.794654);
vec3 c = vec3(0,0,1);

vec3 vertices[20] =
  vec3[](vec3(0,0,1), // 0
         vec3(0.57735,0.333333,0.745356), // 0.713644
         vec3(-0.57735,0.333333,0.745356), // 0.713644
         vec3(0,-0.666667,0.745356), // 0.713644
         vec3(0.356822,0.872678,0.333333), // 1.1547
         vec3(0.934172,-0.127322,0.333333), // 1.1547
         vec3(-0.356822,0.872678,0.333333), // 1.1547
         vec3(-0.934172,-0.127322,0.333333), // 1.1547
         vec3(0.57735,-0.745356,0.333333), // 1.1547
         vec3(-0.577351,-0.745356,0.333333), // 1.1547
         vec3(0.57735,0.745356,-0.333334), // 1.63299
         vec3(0.934172,0.127322,-0.333334), // 1.63299
         vec3(-0.57735,0.745356,-0.333334), // 1.63299
         vec3(-0.934172,0.127322,-0.333334), // 1.63299
         vec3(0.356822,-0.872678,-0.333334), // 1.63299
         vec3(-0.356822,-0.872678,-0.333334), // 1.63299
         vec3(0,0.666666,-0.745356), // 1.86834
         vec3(0.57735,-0.333334,-0.745356), // 1.86834
         vec3(-0.57735,-0.333334,-0.745356), // 1.86834
         vec3(0,0,-1) // 2
         );

vec4 makeplane(vec3 p, vec3 q, vec3 r) {
  vec3 n = cross(q-p,r-p);
  n = normalize(n);
  float k = dot(p,n);
  // Fix so that away from origin is +ve distance
  if (k < 0.0) { k = -k; n = -n; }
  return vec4(n,k);
}

// Could use an ivec3 and count different types of flip
vec3 fold(vec3 pos, out int flips, out mat3 trans) {
  mat3 mA = mat3(reflect(vec3(1,0,0),A),
                 reflect(vec3(0,1,0),A),
                 reflect(vec3(0,0,1),A));
  mat3 mB = mat3(reflect(vec3(1,0,0),B),
                 reflect(vec3(0,1,0),B),
                 reflect(vec3(0,0,1),B));
  mat3 mC = mat3(reflect(vec3(1,0,0),C),
                 reflect(vec3(0,1,0),C),
                 reflect(vec3(0,0,1),C));
  // We want to know the number of actual reflections made
  // so keep track of that in flips.
  flips = 0;
  trans = mat3(1,0,0,
               0,1,0,
               0,0,1);
  for (int i = 0; i < NFOLDS; i++) {
    float k;
    k = dot(pos,A);
    if (k < 0.0) {
      flips += 1;
      pos -= 2.0*k*A;
      trans = trans*mA; // Note order
    }
    k = dot(pos,B);
    if (k < 0.0) {
      flips += 1;
      pos -= 2.0*k*B;
      trans = trans*mB;
    }
    k = dot(pos,C);
    if (k < 0.0) {
      flips += 1;
      pos -= 2.0*k*C;
      trans = trans*mC;
    }
  }
  return pos;
}

ivec2 combos[] =
  // Seem to be 23 different "excavations" with a single plane,
  // excluding the dodecahedron itself and a couple where the face
  // goes through the centre, so the volume is zero.
  ivec2[](ivec2(2,10),ivec2(10,11),ivec2(2,5),ivec2(6,8),ivec2(11,13),ivec2(12,17),ivec2(14,16), // Stars
          ivec2(1,12),ivec2(1,7),
          ivec2(4,5),ivec2(3,4),ivec2(11,14),ivec2(15,19),ivec2(3,11),ivec2(9,19),ivec2(8,10), // Pentagram sides
          ivec2(4,7),ivec2(4,9),ivec2(4,19),ivec2(9,10),ivec2(10,18),ivec2(15,16), // Incised pentagrams
          //ivec2(1,19),ivec2(3,16), // Face through centre - no interesting combinations
          ivec2(10,12) // Compound
          );

float faces(vec3 pos, int t, out vec3 n) {
  int N = combos.length();
  float sgn = dosubtract ? -1.0 : 1.0;
  int X = combos[t%N].x;
  int Y = combos[t%N].y;
  int X1 = combos[t/N%N].x;
  int Y1 = combos[t/N%N].y;
  //int X1 = combos[5].x;
  //int Y1 = combos[5].y;
  //int X2 = combos[0].x;
  //int Y2 = combos[0].y;
  vec4 plane0 = makeplane(vertices[0],vertices[X],vertices[Y]);
  vec4 plane1 = makeplane(vertices[0],vertices[X1],vertices[Y1]);
  //vec4 plane2 = makeplane(vertices[0],vertices[X2],vertices[Y2]);
  float d = -1e8,d1;
  d1 = dot(vec4(pos,-1),plane0);
  if (d1 > d) { d = d1; n = plane0.xyz; }
  d1 = sgn*dot(vec4(pos,-1),plane1);
  if (d1 > d) { d = d1; n = sgn*plane1.xyz; }
  //d1 = dot(vec4(pos,-1),plane2);
  //if (d1 > d) { d = d1; n = plane2.xyz; }
  d1 = dot(pos,b)-dot(b,c); // Clip to dodecahedron
  if (d1 > d) { d = d1; n = b; }
  return d;
}

#if 0
bool chiral = false; // Chiral needs some work.
float faces(vec3 pos, int parity) {
  float d;
  if (!chiral || parity == 0) {
    d = faces0(pos);
  } else {
    d = min(faces0(reflect(pos,A)),
            min(faces0(reflect(pos,B)),
                faces0(reflect(pos,C))));
  }
  d = max(d,(dot(pos,b)-dot(b,c))); // Clip to dodecahedron
  return d;
}
#endif

float polyhedron(vec3 pos, out vec3 n, out vec2 uv) {
  int flips = 0;
  vec3 pos0 = pos;
  mat3 m; // Rotation matrix to apply to normal
  pos = fold(pos,flips,m);
  uv = pos.xy;
  int parity = flips%2;
  float t = iTime;
  vec3 n1,n2;
  float d1 = faces(pos,int(t)/4,n1);
  float d2 = faces(pos,(int(t)+1)/4,n2);
  float d = mix(d1,d2,fract(t));
  n = mix(n1,n2,fract(t));
  n = m*n; // Transform to world coordinates
  normalize(n); // Keep normalized!
  if (doclip && pos0.x > d) {
    d = pos0.x;
    n = vec3(1,0,0);
  }
  return d;
}

//-------------------------------------------------
//From https://www.shadertoy.com/view/XtXGRS#
vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

float map(vec3 p) {
  vec3 n; vec2 uv; // Don't need these here - hopefully get optimized away.
  return polyhedron(p,n,uv);
}

vec3 calcnormal(vec3 p) {
  const vec2 e = vec2(0.001, 0.0);
  vec3 n = vec3(map(p + e.xyy) - map(p - e.xyy),
                map(p + e.yxy) - map(p - e.yxy),
                map(p + e.yyx) - map(p - e.yyx));
  return normalize(n);
}

float march(in vec3 ro, in vec3 rd) {
  float maxd = length(ro) + 1.0;
  float precis = 0.001;
  float t = 0.0;
  float res = -1.0;
  for (int i = 0; i < 100; i++) {
    //assert(i < 20);
    float h = map(ro+rd*t);
    if (abs(h) < precis) return t;
    t += h;
    if (t < 0.0 || t > maxd) break;
  }
  return -1.0;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (dorotate) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.2);
  }
  return p;
}

bool keypress(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

vec4 store(int i,int j) {
  return texelFetch(iChannel1, ivec2(i,j),0);
}

int keycount(int key) {
  return int(store(0,key).x);
}

const int CHAR_C = 67;
const int CHAR_N = 78;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;
const int KEY_UP = 38;
const int KEY_DOWN = 40;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  dorotate = !keypress(CHAR_R);
  doclip = keypress(CHAR_C);
  dosubtract = keypress(CHAR_S);
  bool dotexture = keypress(CHAR_T);
  bool calcnormals = keypress(CHAR_N);
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  vec3 col = vec3(0.1 - 0.1*p.y);
  p *= 0.5;
  float camera = 4.0;
  camera *= 1.0 + 0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP));
  vec3 ro = vec3(0.0, 0.0, camera);
  vec3 rd = vec3(p, -2.0);
  vec3 light = normalize(vec3(0.5, 1.0, 3.0));
  ro = transform(ro);
  rd = transform(rd);
  light = transform(light);
  rd = normalize(rd);
  float t = march(ro, rd);
  if (t > 0.0) {
    vec3 pos = ro + t * rd;
    vec2 uv; vec3 n;
    polyhedron(pos,n,uv);
    if (calcnormals) n = calcnormal(pos);
    //assert(dot(n,rd) <= 0.0);
    col = vec3(0.5, 0.5, 0.3);
    if (dotexture) col = texture(iChannel2,uv).xyz;
    float diffuse = clamp(dot(n, light), 0.0, 1.0);
    col *= 0.2+0.8*diffuse;
    float specular = pow(max(0.0,dot(reflect(light,n),rd)),50.0);
    col += 0.3*specular*vec3(1);
  }
  col = pow(col, vec3(0.4545));
  if (alert) col.x = 1.0;
  fragColor = vec4(col, 1.0);
}

