const float PI = 3.1415926;
const float E = 0.005;

const vec3 light_dir = normalize(vec3(-0.6, 0.2, -1.0));
const vec3 light_color = vec3(0.3, 0.6, 1.0);

struct Ray
{
    vec3 pos;
    vec3 dir;
};

mat2 rotate2D(float rad)
{
    float c = cos(rad);
    float s = sin(rad);
    return mat2(c, s, -s, c);
}

// https://iquilezles.org/articles/smin
vec2 smin(float d1, float d2, float k)
{
    float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return vec2(mix(d2, d1, h) - k * h * (1.0 - h), h);
}

// https://gaz.hateblo.jp/entry/2019/05/17/231141
vec2 spfold4(vec2 p, float k)
{
    const int n = 4;
    for(int i = 0; i < n; i++)
    {
        float a = PI / exp2(float(i));
        vec2 v = vec2(cos(a), sin(a));  
        p -= 2.0 * smin(0.0, dot(p, v), k).x * v;
    }
    return p;
}

float deBody(vec3 p, float t)
{
    float a = atan(p.z, p.x);
    p *= vec3(1.0, 0.7, 1.0);
    p += vec3(0.0, 0.7 - sin(t) * 0.25, 0.0);
    float d = length(p) - 0.9 - sin(a * 20.0) * 0.05;
    d = abs(d) - 0.2;
    return d;
}

float deLeg(vec3 p, float h, float r)
{
    float a = atan(p.z, p.x);
    p.y -= clamp(p.y, 0.0, h);
    float d = length(p) - r - sin(a * 10.0) * pow(r, 0.8) * 0.05;
    return d;
}

float deOctopus(vec3 p)
{
    float t = iTime - pow(length(p), 0.5) * 1.5;

    p.y *= -1.0;
    p.xz *= rotate2D(0.45);
    p.yz *= rotate2D(1.2 + sin(iTime * 0.333) * 0.25);

    float body = deBody(p, t);

    p.xz *= rotate2D(0.4);
    p.xz = spfold4(p.xz, 0.04);
    float h = 5.0;
    float r = exp(-length(p) * 0.7) * 0.75;
    mat2 m = rotate2D(0.35 + cos(length(p) * 1.75 + t) * 0.1);
    p.yz *= m;
    float leg = deLeg(p, h, r);
    p.yz *= m;
    leg = smin(leg, deLeg(p, h, r), 0.04).x;

    float d = smin(body, leg, 0.3).x;
    d *= 0.5;

    return d;
}

float deSea(vec3 p)
{
    float t = iTime - pow(length(p), 0.5) * 1.5;

    p.xz *= rotate2D(t);
    float d = p.y + 0.2
        - sin(p.x * 2.0) * 0.05
        - sin(p.z * 3.111) * 0.05
        - (sin(t * 0.333) * 0.5 + 0.5) * 0.5;
    return d;
}

vec2 de(vec3 p)
{
    vec2 o = smin(deOctopus(p), deSea(p), 0.1);
    return o;
}

float sss(vec3 o, vec3 dir, float ed, float la)
{
    const int ei = 4;
    float accum = 0.0;
    float st = ed / float(ei);
    float d = st;
    for (int i = 0; i < ei; i++)
    {
        accum += max(de(o + dir * d).x, 0.0);
        d += st;
    }
    accum = clamp(accum / ed / (float(ei) * 0.5 + 0.5), 0.0, 1.0);
    return exp(-(1.0 - accum) * la);
}

float luminous(vec3 o, vec3 dir, float ed, float la)
{
    const int ei = 16;
    float accum = 0.0;
    float st = ed / float(ei);
    float d = st;
    for (int i = 0; i < ei; i++)
    {
        accum += max(-de(o + dir * d).x / d, 0.0);
        d += st;
    }
    accum = clamp(accum / float(ei), 0.0, 1.0);
    return exp(-(1.0 - accum) * la);
}

// iquilezles.org/articles/normalsSDF
vec3 normal(vec3 p)
{
    float h = E;
    vec2 k = vec2(1.0, -1.0);
    return normalize(
            k.xyy * de(p + k.xyy * h).x + 
            k.yyx * de(p + k.yyx * h).x + 
            k.yxy * de(p + k.yxy * h).x + 
            k.xxx * de(p + k.xxx * h).x
        );
}

void trace(Ray ray, inout vec3 color, float md)
{
    float ad = 0.0;
    for (float i = 1.0; i > 0.0; i -= 1.0 / 150.0)
    {
        vec2 o = de(ray.pos);
        if (o.x < E)
        {
            // normal
            vec3 n = normal(ray.pos);

            // fresnel
            float f0 = 0.075;
            float f = f0 + (1.0 - f0) * pow(1.0 - max(dot(-ray.dir, n), 0.0), 5.0);

            // color
            vec3 em = vec3(0.1, 1.0, 0.3);

            vec3 color1 = luminous(ray.pos, ray.dir, 0.35, 7.5) * em * 60.0;
            color1 += sss(ray.pos, light_dir, 0.35, 5.0) * light_color * 5.0;

            vec3 color2 = sss(ray.pos, ray.dir, 0.5, 5.0) * vec3(0.0, 0.0, 1.0) * 2.0 * f;
            color2 += sss(ray.pos, light_dir, 0.5, 6.0) * light_color * 0.3 * f;
            color2 += exp(-deOctopus(ray.pos) * 17.5) * (em + light_color) * 0.01 * exp(-length(ray.pos) * 0.25);

            color += mix(color2, color1, pow(o.y, 5.0));

            color *= exp(-ad * ad * 0.03);

            return;
        }

        ray.pos += ray.dir * o.x;
        ad = ad + o.x;
        if (ad > md)
        {
            break;
        }
    }

    // background
    color += pow(max(dot(ray.dir, light_dir), 0.0), 800.0) * light_color * 3.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);

    // view
    vec3 view = vec3(-1.0, 1.0, 6.75);
    vec3 at = normalize(vec3(-2.0, 0.0, 0.0) - view);
    vec3 right = normalize(cross(at, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, at);
    float focallength = 2.25;

    // ray
    Ray ray;
    ray.pos = view;
    ray.dir = normalize(right * p.x + up * p.y + at * focallength);

    // ray marching
    trace(ray, color, 20.0);

    // cheap tonemapping
    // https://www.desmos.com/calculator/adupt0spl8
    float k = 0.75;
    color = mix(color, 1.0 - exp(-(color - k) / (1.0 - k)) * (1.0 - k), step(k, color));

    // gamma correction
    color = pow(color, vec3(0.454545));

    fragColor = vec4(color, 1.0);
}

// "Helical Ramps with Spiders" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define N_SPD  5  // Number of spiders per turn (1-20, roughly)

#define AA  0   // optional antialiasing

#define VAR_ZERO min (iFrame, 0)

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrEETapCylDf (vec3 p, vec3 v1, vec3 v2, float r, float rf);
float PrEllipsDf (vec3 p, vec3 r);
float Maxv3 (vec3 p);
mat3 StdVuMat (float el, float az);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Fbm2 (vec2 p);

vec3 footPos[8], kneePos[8], hipPos[8], ltDir, qHit;
float tCur, dstFar, legLenU, legLenD, bdyHt, spdVel, nSpd, vGap, radExt, radRmp;
int idObj;
const int idRmp = 1, idCol = 2, idWalI = 3, idWalM = 4, idWalO = 5, idBm = 6,
   idBdy = 11, idHead = 12, idEye = 13, idAnt = 14, idLegU = 15, idLegD = 16;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float SpdDf (vec3 p, float dMin, int tpId)
{
  vec3 q;
  float d, s, len, szFac;
  szFac = 0.22;
  p /= szFac;
  dMin /= szFac; 
  p.y -= bdyHt + 0.8;
  q = p - vec3 (0., -0.15, 0.2);
  d = PrEllipsDf (q, vec3 (0.7, 0.5, 1.3));
  DMINQ (idBdy + tpId);
  q = p - vec3 (0., 0.1, 1.1);
  d = PrEllipsDf (q, vec3 (0.2, 0.4, 0.5));
  DMINQ (idHead + tpId);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.15, 0.25, 1.5);
  d = PrSphDf (q, 0.13);
  DMINQ (idEye + tpId);
  q -= vec3 (0., 0.15, -0.3);
  d = PrEETapCylDf (q, 1.3 * vec3 (0.3, 1.1, 0.4), vec3 (0.), 0.07, 0.7);
  DMINQ (idAnt + tpId);
  p.y += bdyHt;
  for (int j = VAR_ZERO; j < 8; j ++) {
    q = p - hipPos[j];
    d = 0.6 * PrEETapCylDf (q, kneePos[j], hipPos[j], 0.25, 0.3);
    DMINQ (idLegU + tpId);
    q = p - kneePos[j];
    d = 0.6 * PrEETapCylDf (q, footPos[j] - vec3 (0.3), kneePos[j] - vec3 (0.3), 0.2, 1.2);
    DMINQ (idLegD + tpId);
  }
  return dMin * szFac;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, widRmp, aRot, r, a, ai, s;
  dMin = dstFar;
  widRmp = 1.2;
  r = length (p.xz);
  a = (r > 0.) ? atan (p.z, - p.x) / (2. * pi) + 0.5 : 0.;
  q = p;
  q.y = mod (q.y + (0.5 - 2. * a) * vGap, vGap) - 0.5 * vGap;
  d = PrBox2Df (vec2 (r - radRmp, q.y), vec2 (widRmp, 0.1));
  DMIN (idRmp);
  d = max (PrBox2Df (vec2 (r - 0.5 * radRmp, q.y + 0.2), vec2 (0.1, 0.5)),
     - PrBox2Df (vec2 (abs (mod (7. * a, 1.) - 0.5), q.y + 0.2), vec2 (0.3, 0.2)));
  DMIN (idWalI);
  d = PrBox2Df (vec2 (abs (r - radRmp) - widRmp, q.y - 0.2), vec2 (0.07, 0.3));
  DMIN (idWalM);
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (7. * a + 0.5) / 7. - 0.5));
  q.x += 0.5 * radRmp;
  d = length (q.xz) - 0.2;
  DMIN (idCol);
  q.x += 0.25 * radRmp + 0.5 * widRmp;
  s = -0.03 * q.x;
  q.y = mod (q.y + 0.15 + (0.5 - 2. * a) * vGap, vGap) - 0.5 * vGap - s;
  d = PrBoxDf (q, vec3 (0.25 * radRmp + 0.5 * widRmp, 0.15 - s, 0.1));
  DMIN (idBm);
  q = p;
  d = max (abs (length (q.xz) - radExt - 0.2) - 0.2,
     - PrBox2Df (mod (vec2 (21. * (0.5 + a), 0.125 * q.y) + 0.5, 1.) - 0.5, vec2 (0.2, 0.3)));
  DMIN (idWalO);
  aRot = atan (vGap / (2. * pi * radRmp));
  for (float k = float (VAR_ZERO); k <= 1.; k ++) {
    s = sign (1. - 2. * k);
    q = p;
    a = s * spdVel * tCur / (2. * pi * radRmp);
    q.xz = Rot2D (q.xz, a);
    ai = 2. * pi * floor (nSpd * ((r > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.) + 0.5) / nSpd;
    q.xz = Rot2D (q.xz, ai);
    q.y = mod (q.y + 0.2 + k * vGap + (1. - (ai + a) / pi) * vGap, 2. * vGap) - vGap;
    q.yz = Rot2D (q.yz, aRot);
    q.x += radRmp;
    q.xz *= s;
    if (PrCylDf (q.xzy, 1., 0.7) < dMin) dMin = SpdDf (q, dMin, int (10. * k));
  }
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.002, -0.002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float dstLim)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.03 * d, h));
    d += clamp (2. * h, 0.05, 0.3);
    if (sh < 0.05 || d > dstLim) break;
  }
  return 0.6 + 0.4 * sh;
}

vec3 SpdCol ()
{
  vec3 col, c[2];
  vec2 cc;
  cc = vec2 (1., 0.2);
  if (idObj >= idBdy + 10) {
    idObj -= 10;
    c[0] = cc.xxy;
    c[1] = cc.xyy;
  } else {
    c[0] = cc.yyx;
    c[1] = cc.yxy;
  }
  if (idObj == idBdy) {
    col = mix (c[0], c[1], SmoothBump (0.2, 0.7, 0.05, mod (4. * qHit.z, 1.)));
  } else if (idObj == idHead) {
    col = c[1];
    if (qHit.z > 0.4) col *= 0.2 + 0.8 * smoothstep (0.02, 0.04, abs (qHit.x));
  } else if (idObj == idEye) {
    col = cc.xxx;
  } else if (idObj == idLegU || idObj == idLegD) {
    col = mix (c[1], c[0],  SmoothBump (0.4, 1., 0.2, fract (3.5 * length (qHit))));
  } else if (idObj == idAnt) {
    col = cc.xyx;
  }
  return col;
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 16. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ShStagGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.25 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, bgCol, vn, rg;
  float dstObj, vDotL, sh, atten, a, s, sa, saa;
  bgCol = StarPat (rd, 12.);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    a = atan (ro.z, - ro.x) / (2. * pi);
    vn = ObjNf (ro);
    if (idObj == idRmp) {
      col4 = vec4 (0.4, 0.4, 0.45, 0.1) * (0.7 + 0.3 * Fbm2 (64. * ro.xz));
    } else if (idObj == idWalI) {
      col4 = vec4 (0.7, 0.7, 0.2, 0.2);
    } else if (idObj == idCol) {
      col4 = vec4 (0.7, 0.7, 0.2, 0.2) * (0.7 + 0.3 * smoothstep (0.05, 0.1, mod (4. * ro.y, 1.)));
    } else if (idObj == idWalM) {
      col4 = (vn.y < 0.9) ? vec4 (0.8, 0.75, 0.75, 0.1) : vec4 (0.7, 0.7, 0.2, 0.2);
      col4.rgb *= (0.7 + 0.3 * smoothstep (0.05, 0.1, 0.5 * (1. + sin (256. * pi * a))));
    } else if (idObj == idBm) {
      col4 = vec4 (0.8, 0.75, 0.75, 0.);
    } else if (idObj == idWalO) {
      col4 = vec4 (0.7, 0.7, 0.6, 0.) * (0.7 + 0.3 * step (length (ro.xz), radExt + 0.01)); 
      s = mod (21. * (0.5 + a) + 0.5, 1.) - 0.5;
      if (abs (s) < 0.08) {
        sa = mod (1.5 * ro.y + 0.5, 1.) - 0.5;
        saa = smoothstep (0., 0.1, abs (sa));
        col4 *= 0.8 + 0.2 * saa;
        vn.y = -0.3 * (1. - saa) * sign (sa);
        vn.xz *= sqrt (1. - vn.y * vn.y);
        vn.xz = Rot2D (vn.xz, asin (pi * clamp (4. * s, -0.5, 0.5)));
      } else {
        rg = ShStagGrid (3. * vec2 (42. * a, ro.y));
        col4 *= rg.y;
        rg.xz *= sign ((abs (vn.x) > 0.5) ? vn.x : vn.z);
        if (abs (vn.x) > 0.5) {
          if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
          else vn.xz = Rot2D (vn.xz, rg.x);
        } else {
          if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
          else vn.zx = Rot2D (vn.zx, rg.x);
        }
      }
    } else {
      col4 = vec4 (SpdCol (), 0.3);
    } 
    vDotL = max (dot (vn, ltDir), 0.);
    if (idObj >= idBdy) vDotL *= vDotL;
    atten = min (1., 1.2 / (1. + 0.2 * dstObj * dstObj / (radExt * radExt)));
    sh = 1.;
    if (idObj != idWalO) sh = ObjSShadow (ro, ltDir, dstObj);
    if (col4.a >= 0.) col = atten * (col4.rgb * (0.2 + 0.8 * sh * vDotL) +
       step (0.95, sh) * col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.));
    else col = atten * col4.rgb;
    col = mix (col, bgCol, smoothstep (0.8, 1., abs (ro.y) / (2. * radExt)));
  } else {
    col = bgCol;
  }
  return clamp (col, 0., 1.);
}

void SpdSetup (float gDisp)
{
  vec3 v;
  float a, az, fz, d, ll;
  nSpd = float (N_SPD);
  for (int j = VAR_ZERO; j < 4; j ++) {
    a = 0.2 * (1. + float (j)) * pi;
    hipPos[j] = 0.5 * vec3 (- sin (a), 0., 1.5 * cos (a));
    hipPos[j + 4] = hipPos[j];
    hipPos[j + 4].x *= -1.;
  }
  bdyHt = 1.5;
  legLenU = 2.2;
  legLenD = 3.;
  ll = legLenD * legLenD - legLenU * legLenU;
  for (int j = VAR_ZERO; j < 8; j ++) {
    fz = fract ((gDisp + 0.93 + ((j < 4) ? -1. : 1.) + mod (7. - float (j), 4.)) / 3.);
    az = smoothstep (0.7, 1., fz);
    footPos[j] = 5. * hipPos[j];
    footPos[j].x *= 1.7;
    footPos[j].y += 0.7 * sin (pi * clamp (1.4 * az - 0.4, 0., 1.));
    footPos[j].z += ((j < 3) ? 0.5 : 1.) - 3. * (fz - az);
    hipPos[j] += vec3 (0., bdyHt - 0.3, 0.2);
    v = footPos[j] - hipPos[j];
    d = length (v);
    a = asin ((hipPos[j].y - footPos[j].y) / d);
    kneePos[j].y = footPos[j].y + legLenD *
       sin (acos ((d * d + ll) / (2. * d *  legLenD)) + a);
    kneePos[j].xz = hipPos[j].xz + legLenU * sin (acos ((d * d - ll) /
       (2. * d *  legLenU)) + 0.5 * pi - a) * normalize (v.xz);
  }
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv, ut, mSize, mMid, msw;
  float el, az, zmFac, asp, sr, regId;
  int vuMode;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  mSize = (1./6.) * vec2 (asp, 1.);
  mMid = vec2 (asp * (1. - mSize.y), - 1. + mSize.y);
  ut = abs (uv - mMid) - mSize;
  vuMode = 0;
  if (max (ut.x, ut.y) < 0.) {
    uv = (uv - mMid) / mSize.y;
    vuMode = 1;
  }
  az = 0.05 * pi * (floor (0.2 * tCur) + smoothstep (0.9, 1., mod (0.2 * tCur, 1.)));
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    regId = 0.;
    msw = (mPtr.xy * vec2 (asp, 1.) - 0.5 * mMid) / mSize;
    if (max (abs (msw.x), abs (msw.y)) < 0.5) regId = 1.;
    else msw = mPtr.xy;
    if (vuMode == 0) {
      az += 2. * pi * msw.x;
      el += pi * msw.y;
    }
    if (regId == 1.) vuMode = 1 - vuMode;
  }
  if (length (uv * vec2 (1. / asp, 1.)) < 1.5 * SmoothBump (0.25, 0.75, 0.1,
     mod (0.03 * tCur, 1.))) vuMode = 1 - vuMode;
  if (vuMode == 0) el = clamp (el, -0.25 * pi, 0.25 * pi);
  vuMat = StdVuMat (el, az);
  spdVel = 2.5;
  SpdSetup (spdVel * tCur);
  radExt = 15.;
  radRmp = 6.;
  vGap = 3.;
  if (vuMode == 0) {
    ro = vuMat * vec3 (0., 0., - (radExt - 0.2));
    zmFac = 2.2;
  } else if (vuMode == 1) {
    ro = vec3 (0.);
    zmFac = 0.5;
  }
  dstFar = 4. * radExt;
  ltDir = vuMat * normalize (vec3 (0.5, 0.5, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi)) / zmFac;
    if (vuMode == 0) rd = normalize (vec3 (uvv, 1.));
    else rd = normalize (vec3 (sin (uvv.x + vec2 (0., 0.5 * pi)), uvv.y).xzy);
    rd = vuMat * rd;
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (vuMode == 1) col = pow (col, vec3 (0.9));
  if (max (ut.x, ut.y) < 0. && min (abs (ut.x), abs (ut.y)) * canvas.y < 2.) col = vec3 (0.2, 0.5, 0.2);
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrEETapCylDf (vec3 p, vec3 v1, vec3 v2, float r, float rf)
{
  vec3 v;
  float s;
  v = v1 - v2;
  s = clamp (dot (p, v) / dot (v, v), 0., 1.);
  return length (p - s * v) - r * (1. - rf * s * s);
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

