    t = mod(t, scenes[0] + scenes[1] /* + scenes[2]*/  );



    if(t < scenes[0]){
        float lsp = 1.3;
        float env = ease(t/scenes[0]*lsp, 1., 1.05, 0.98, 1.);  


        d = min(d, sdCirc(uv - vec2(0,1)*W, dotW));
        d = min(d, sdCirc(uv - vec2(sin(tau*1./3.),cos(tau*1./3.))*W, dotW));
        d = min(d, sdCirc(uv - vec2(sin(tau*2./3.),cos(tau*2./3.))*W, dotW));

        d = 10e6;
        for(float i = 1.; i < 4.; i++){

        }

        vec2 uvo = uv;
        uv.y += W;
        uv *= rot(tau/2.);
        uv *= rot(tau*1./3.);
        uv.y -= W;
        uv *= rot(env*pi*4./3.);
        uv.y += W;

        d = min(d,  sdEquilateralTriangle( uv, triS ));

        uv = uvo;
        uv *= rot(tau/3.);
        uv *= rot(tau*2./3.);
        uv.y -= W;
        uv *= rot(env*pi*4./3. + pi/3.);
        uv.y += W;
        d = min(d,  sdEquilateralTriangle( uv, triS ));

        uv = uvo;
        uv *= rot(tau*3.);
        uv *= rot(tau*1./3.);
        uv.y -= W;
        uv *= rot(env*pi*4./3. + pi/3.);
        uv.y += W;
        d = min(d,  sdEquilateralTriangle( uv, triS ));

    } else if(t < scenes[1] + scenes[0]){
        t -= scenes[0];

        float lsp = 1.8;
        float env = ease(t/scenes[1]*lsp, 1., 1.14, 0.96, 10.)*smoothstep(1.,0.,t/scenes[1]*lsp);

        float mov = env*0.3;

        d = min(d, sdCirc(uv - vec2(0,1)*W, dotW));
        d = min(d, sdCirc(uv - vec2(sin(tau*1./3.),cos(tau*1./3.))*W, dotW));
        d = min(d, sdCirc(uv - vec2(sin(tau*2./3.),cos(tau*2./3.))*W, dotW));

        d = 10e6;

        vec2 uvo = uv;
        uv.y += W;


        uv.y += mov;
        uv *= rot(tau/2.);
        uv *= rot(tau*1./3.);
        d = min(d,  sdEquilateralTriangle( uv, triS ));

        uv = uvo;
        uv *= rot(tau/3.);
        uv.y += mov;
        uv *= rot(tau*2./3.);
        uv.y -= W;
        uv *= rot(pi/3.);
        uv.y += W;
        d = min(d,  sdEquilateralTriangle( uv, triS ));

        uv = uvo;
        uv *= rot(tau*1./3.);
        uv.y -= W;
        uv *= rot(pi/3.);
        uv.y += mov*0.5;
        uv.x += mov;
        uv.y += W;
        d = min(d,  sdEquilateralTriangle( uv, triS ));


    }  

    return vec3(d);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;  
    vec3 col = vec3(0);

    float edge = dFdx(uv.x)*1.;

    for (int i = 0; i < motionBlurSteps + min(iFrame,0); ++i) {
        float subsample = bayer8(ivec2(fragCoord));
        float time = iTime + ((float(i)+subsample)/float(motionBlurSteps)-.5) * timeStep;
        col += smoothstep(-edge,edge,-get(uv,time));
    }
    col/=float(motionBlurSteps);

    col = clamp(col, 0., 1.);
    col *= 0.999;

    col *= max((1. + 1.*cos(sin(0.25*tau*iTime/(scenes[0] + scenes[1] ))*tau+length(uv)*2.+vec3(4,2.5,1.5)))*1., 0.);
    //col *= max((1.5 + 1.4*cos(iTime+length(uv)*2.+vec3(1,1.5,1.5)))*1., 0.);

    col *= 1. - pow(smoothstep(0.,1.,dot(uv,uv)*0.9),0.9)*1.;

    col = pow(col, vec3(0.454545));

    fragColor = vec4(col,1.0);
}

uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u); //large odd digits, 0 and 1 are fully mixed

float hash11(uint n){
    n ^= (n << 9);
    n ^= (n >> 1);
    n ^= (n << 1);
    n *= k.x;
    return float(n) / float(0xffffffffu);
}
vec2 hash22(uvec2 n){
    n ^= (n.yx << 9);
    n ^= (n.yx >> 1);
    n *= k.xy;
    n ^= (n.yx << 1);
    n *= k.xy;
    return vec2(n) / float(0xffffffffu);
}
vec3 hash33(uvec3 n) {
    n ^= (n.yzx << 9);
    n ^= (n.yzx >> 1);
    n *= k;
    n ^= (n.yzx << 1);
    n *= k;
    return vec3(n) / float(0xffffffffu);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy + vec2(iFrame);
    vec3 col;
    if (fragCoord.x < 0.33 * iResolution.x){ 
        col = vec3(hash11(floatBitsToUint(p.x)));
    } else if (fragCoord.x < 0.66 * iResolution.x){
        col = vec3(hash22(floatBitsToUint(p)), 1.0);
    } else {
        col = vec3(hash33(floatBitsToUint(vec3(p, iFrame))));
    }
    fragColor = vec4(col, 1.0);
}

