const float Angle = 45.0;
const float Exposure = exp2(-3.0);

const int BlurSamples = 9;
const float BlurScale = 2.3;
const float BlurBrightness = 0.5;

const float Gamma = 2.333333;

const float Pi = 3.1415926;
const float Deg2Rad = Pi / 180.0;
const float Rad2Deg = 180.0 / Pi;

vec2 ScaleCoord(vec2 coord, vec2 scale, vec2 pivot)
{
    return (coord - pivot) * scale + pivot;
}

// https://en.wikipedia.org/wiki/Rotation_%28mathematics%29#Two_dimensions
vec2 Rotate(vec2 p, float deg)
{
    float rad = Deg2Rad * deg;
    float s = sin(rad);
    float c = cos(rad);

    vec2 res;
    res.x = p.x * c - p.y * s;
    res.y = p.x * s + p.y * c;

    return res;
}

float AngleBetween(vec2 a, vec2 b)
{
    vec2 d = b - a;
    float angle = atan(d.y, d.x);
    angle *= Rad2Deg;

    return angle; 
}

vec4 Blur(
    sampler2D sp,
    vec2 uv,
    vec2 dir,
    const int samples,
    float bright)
{
    if (samples < 1)
        return vec4(0.0);

    if (samples == 1)
        return texture(sp, uv);

    uv -= float(samples / 2) * dir;

    vec4 color = texture(sp, uv);
    vec4 maxColor = color;

    for (int i = 1; i < samples; ++i)
    {
        uv += dir;

        vec4 pixel = texture(sp, uv);
        color += pixel;
        maxColor = max(maxColor, pixel);
    }

    color /= float(samples);

    color = mix(color, maxColor, color * bright);

    return color;
}

float GetScale(vec2 uv, float t)
{
    float scale = sin(uv.x + uv.y + t * 0.6) * 0.5 + 0.5;
    scale = pow(abs(scale), 0.9) * 6.0;
    scale = min(scale, 1.0);

    return scale;
}

float GetScale(vec2 uv, float t, vec4 mouse, vec2 ps)
{
    float scale = BlurScale;

    if (mouse.z < 1.0)
        return BlurScale * GetScale(uv, t);

    scale *= distance(mouse.xy * ps, vec2(0.5)) * 3.0;

    return scale;
}

float GetAngle(vec4 mouse, vec2 ps)
{
    if (mouse.z < 1.0)
        return Angle;

    float angle = 90.0 - AngleBetween(mouse.xy * ps, vec2(0.5));

    return angle;
}

vec3 Reinhard(vec3 color)
{
    return color / (1.0 + color);
}

vec3 ReinhardInv(vec3 color)
{
    return -(color / min(color - 1.0, -0.1));
}

vec3 UE3Tonemap(vec3 color)
{
    return color / (color + 0.155) * 1.019;
}

vec3 UE3TonemapInv(vec3 color)
{
    return (color * -0.155) / (max(color, 0.01) - 1.019);
}

vec3 BakingLabACESTonemap(vec3 color, bool isInverse)
{
	const float A = 0.0245786;
    const float B = 0.000090537;
    const float C = 0.983729;
    const float D = 0.4329510;
    const float E = 0.238081;

    if (!isInverse)
    {
        color = (
            (color * (color + A) - B) /
            (color * (C * color + D) + E));
        color = clamp(color, 0.0, 1.0);

        return color;
    }

    color = abs(
        (
            (A - D * color) -
            sqrt(
                pow(abs(D * color - A), vec3(2.0)) -
                4.0 * (C * color - 1.0) * (B + E * color))
        ) /
        (2.0 * (C * color - 1.0))
    );

    return color;
}

vec3 Tonemap(vec3 color)
{
    color = Reinhard(color);
    color = pow(abs(color), vec3(1.0 / Gamma));

    return color;
}

vec3 InvTonemap(vec3 color)
{
    color = pow(abs(color), vec3(Gamma));
    color = ReinhardInv(color);

    return color;
}

