void mainImage(out vec4 color, vec2 coord)
{
    vec2 ps = 1.0 / vec2(textureSize(iChannel0, 0)) * 3.0;

    vec2 uv0 = coord;
    uv0.x += iTime * 10.0;
    uv0.y -= iTime * 2.0;
    uv0 *= ps * 0.4;

    vec4 color0 = texture(iChannel0, uv0);

    vec2 uv1 = coord;
    uv1.x -= iTime * 20.0;
    uv1.y += iTime * 30.0;
    uv1 *= ps * 0.3;

    vec4 color1 = texture(iChannel0, uv1);

    color = max(color0, color1);

    color.rgb = InvTonemap(color.rgb);
}

