void mainImage(out vec4 color, vec2 coord)
{
    vec2 ps = 1.0 / iResolution.xy;
    vec2 uv = coord * ps;

    float angle = GetAngle(iMouse, ps);

    vec2 dir = Rotate(vec2(0, 1), -angle);
    dir *= ps * GetScale(uv, iTime, iMouse, ps);

    color = Blur(
        iChannel0,
        uv,
        dir,
        BlurSamples,
        BlurBrightness);
}

