#define S(x,y,z) smoothstep(x,y,z)
#define AR (iResolution.x/iResolution.y)
#define PI (3.1415)

#define TEXT_COLOR vec3(0.8,0.8,0.8)
#define HIGHLIGHT_COLOR vec3(0.05,0.5,0.8)
#define LIGHT_BG vec3(0.1,0.1,0.1)
#define LIGHT_TEXT_COLOR vec3(0.1,0.1,0.1)
#define VOTEUP_COLOR vec3(255.0/255.0, 139.0/255.0, 90.0/255.0)

// https://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
// All components are in the range [0…1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 inverseColor(vec3 col)
{
    return vec3(1.0-col.r,1.0-col.g,1.0-col.b);
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float rand(float c){
    vec2 co = vec2(c,c);
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 rotate(vec2 uv, float angle)
{
    uv -= 0.5;
    mat2 mat = mat2(vec2(cos(angle), sin(angle)),vec2(-sin(angle), cos(angle)));
    return mat*uv + 0.5;
}

// -------------b
// |			|
// |			|
// a-------------
float inRect(vec2 x ,vec2 a, vec2 b)
{
    return x.x > a.x && x.x < b.x && x.y > a.y && x.y < b.y ? 1.0 : 0.0;
}

float rectMask(vec2 uv, vec2 a, vec2 b)
{
    float lb = 0.01;
    float m = S(a.x,a.x+lb,uv.x);
    m = min(S(b.x+lb,b.x,uv.x),m);
    m = min(S(a.y,a.y+lb,uv.y),m);
    m = min(S(b.y+lb,b.y,uv.y),m);
    return m;
}

float inUVRange(vec2 uv)
{
    return inRect(uv,vec2(0.0),vec2(1.0));
}

// UV is already 0.0-1.0 i assume
vec2 uvToRect(vec2 uv, vec2 a, vec2 b)
{
    uv -= a;
    uv /= (b-a);
    return uv;
}

float roundBoxMask( vec2 uv, vec2 size, float radius )
{
  return inRect(uv, vec2(0.1),vec2(0.9));
}

float searchIconMask(vec2 uv)
{
    float d = length(uv+vec2(0.1,-0.1)-vec2(0.5));

    float cs = 0.3; // Circle size
    float lt = 0.1; // Line thickness

    float cm = S(cs+0.01,cs,d); // Circle mask
    cm -= S(cs-lt+0.01,cs-lt,d);
    cm = clamp(cm,0.0,1.0);

    float lm = S(1.0,1.01,uv.x+uv.y); // Line mask
    lm = min(lm,S(1.01+lt,1.00+lt,uv.x+uv.y));
    lm *= S(0.2,0.21,uv.x-uv.y);
    lm *= S(0.86,0.85,uv.x-uv.y);

    return cm + lm;
}

float redditIconMask(vec2 uv)
{
    uv = uvToRect(uv,vec2(0.2,0.2),vec2(0.8,0.8));

    float iconMask = 0.0;

    // Head
    vec2 headUV = uv - vec2(0.5,0.4);
    float a = 1.2;
    float b = 0.9;
    float d = (headUV.x*headUV.x)/(a*a) + (headUV.y*headUV.y)/(b*b);
    iconMask = S(0.11,0.1,d);

    // Ears
    vec2 earPos = vec2(0.35,0.13);
    float earSize = 0.12;
    iconMask += S(earSize+0.01,earSize, length(headUV - earPos));
    iconMask += S(earSize+0.01,earSize, length(headUV - earPos*vec2(-1.0,1.0)));
    iconMask = min(1.0,iconMask);

    // Antenna
    float lineThickness = 0.02;
    float lineMask = S(0.5-lineThickness,0.5-lineThickness+0.01,uv.x);
    lineMask = min(lineMask,S(0.5+lineThickness+0.01,0.5+lineThickness,uv.x));
    lineMask *= S(0.6,0.61,uv.y);
    lineMask *= S(0.84,0.83,uv.y);
    vec2 dLinePos = vec2(1.0,-2.0);
    lineThickness *= 2.0;
    float lineHeight = -1.14;
    float dLineMask = S(lineHeight-lineThickness,lineHeight-lineThickness+0.01,dLinePos.x*uv.x+dLinePos.y*uv.y);
    dLineMask = min(dLineMask,S(lineHeight+lineThickness+0.01,lineHeight+lineThickness,dLinePos.x*uv.x+dLinePos.y*uv.y));
    dLineMask *= S(0.48,0.49,uv.x);
    dLineMask *= S(0.61,0.6,uv.x);
    float circleAntennaSize = 0.07;
    vec2 circlePos = vec2(0.65,0.89);
    float circleMask = S(circleAntennaSize+0.01,circleAntennaSize,length(uv-circlePos));

    iconMask += lineMask+dLineMask+circleMask;
    iconMask = min(iconMask,1.0);


    // Eyes
    //vec2 eyePos = vec2(0.15,0.06);
    //float eyeSize = 0.07;
    //iconMask -= S(eyeSize+0.01,eyeSize, length(headUV - eyePos));
    //iconMask -= S(eyeSize+0.01,eyeSize, length(headUV - eyePos*vec2(-1.0,1.0)));
    //iconMask = max(0.0,iconMask);

    return iconMask;
}

float communityIconMask(vec2 uv)
{
    uv = uvToRect(uv,vec2(0.2,0.2),vec2(0.8,0.8));

    float m = 0.0;

    vec2 ruv = uvToRect(uv,vec2(0.1,0.1),vec2(0.4,0.4));
    m += inUVRange(ruv)*S(0.51,0.5,length(ruv-vec2(0.5)));

    ruv = uvToRect(uv,vec2(0.6,0.1),vec2(0.9,0.4));
    m += inUVRange(ruv);

    ruv = uvToRect(uv,vec2(0.6,0.6),vec2(0.9,0.9));
    m += inUVRange(ruv)*S(0.51,0.5,length(ruv-vec2(0.5)));

    ruv = uvToRect(uv,vec2(0.1,0.6),vec2(0.4,0.9));
    m += inUVRange(ruv);

    return m;
}

float pencilIconMask(vec2 uv)
{
    uv = uvToRect(uv, vec2(0.15,0.2),vec2(0.8,0.8));

    float m = 0.0;
    float pt = 0.07; // pencil thicness

    uv -= vec2(0.2,0.7);
    float d = (0.9*uv.x - uv.y);
    m = S(0.5-pt,0.5-pt+0.01,d);
    m = min(m,S(0.5+pt+0.01,0.5+pt,d));

    d = (uv.x + uv.y);
    m -= S(0.7,0.71,d);
    m -= S(0.07,0.06,uv.x);
    m -= S(-0.45,-0.455,uv.y);
    m -= min(S(0.55,0.56,d), S(0.60,0.59,d));
    m = max(m,0.0);

    return m;
}

float chatIconMask(vec2 uv)
{
    uv = uvToRect(uv,vec2(0.1,0.1),vec2(0.9,0.9));

    float iconSize = 0.3;

    // Bubble
    float m = S(iconSize+0.01,iconSize,length(uv-vec2(0.5,0.5)));

    // Bubble end
    float x = 0.6;
    vec2 buv = uv - vec2(0.2,0.1);
    float be = S(0.5,0.51,x+0.3*buv.x-buv.y);
    be += S(0.51,0.5,x+0.75*buv.x-buv.y);
    be = 1.0 - min(1.0,be);
    be *= S(0.51,0.5,uv.x);
    m += be;
    m = min(1.0,m);

    // Dots
    vec2 duv = uvToRect(uv,vec2(0.4,0.45),vec2(0.6,0.55));
    float dm = inUVRange(duv);
    duv.x = fract(duv.x*3.0);
    m -= S(0.31,0.3,length(duv-vec2(0.5)))*dm;
    m = max(0.0,m);

    return m;
}

float inboxIconMask(vec2 uv)
{
    uv = uvToRect(uv,vec2(0.2,0.2),vec2(0.8,0.8));

    float m = 0.0;

    m = rectMask(uv,vec2(0.1,0.2),vec2(0.9,0.8));

    float lt = 0.01;
    float d = (uv.x+uv.y)/2.0;
    float lp = 0.45;
    float dl = min(S(lp-lt,lp-lt+0.005,d),S(lp+lt+0.005,lp+lt,d));
    d = (uv.x-uv.y);
    lt *= 4.0;
    lp = 0.06;
    dl += min(S(lp,lp+0.01,d),S(lp+lt+0.01,lp+lt,d));
    dl = min(1.0,dl);
    dl *= S(0.415-0.001,0.415,uv.y);

    m -= dl;
    m = max(0.0,m);

    return m;
}

float upvoteIconMask(vec2 uv)
{
    uv = uvToRect(uv,vec2(0.1),vec2(0.9));

   	vec2 auv = uv - vec2(0.0,0.5);
    float m = S(0.0,0.01,auv.x-auv.y);
    auv = uv - vec2(0.0,1.0);
    m = min(S(0.51,0.5,(auv.x+auv.y)),m);
    m *= S(0.5,0.505,uv.y);

    m += rectMask(uv,vec2(0.3,0.0),vec2(0.7,0.6));
    m = min(1.0,m);

    return m;
}

float commentsIconMask(vec2 uv)
{
    uv = uvToRect(uv,vec2(0.0,0.2),vec2(1.0,1.0));

    vec2 auv = uv - vec2(0.5,0.0);
    float m = S(0.01,0.00,auv.x-auv.y);
    auv = uv + vec2(0.5,0.0);
    m = min(S(1.0,1.01,auv.x+auv.y),m);
    m *= S(0.21,0.2,uv.y);
    m += rectMask(uv,vec2(0.15,0.15),vec2(0.85,0.65));
    m = min(m,1.0);

    return m;
}

vec3 drawRedditHeader(vec2 uv)
{
    float pihs = 0.35; // Profile icon header size

    float bgMask = 1.0;

    // Profile icon
    vec2 profileIconUV = uvToRect(uv,vec2(0.05,0.5),vec2(pihs+0.05,1.0));
    profileIconUV.x /= pihs;
    float profileIconSize = 0.4;
    float profileIconMask = S(profileIconSize+0.01,profileIconSize,length(profileIconUV-vec2(0.5)));
    vec3 profileIconColor = hsv2rgb(vec3(rand(iDate.x),0.7,0.6));
    vec3 profileIcon = profileIconColor*profileIconMask;

    bgMask -= profileIconMask;

   	// Search bar
    pihs -= 0.1;
    vec2 searchBarUV = uvToRect(uv,vec2(pihs,0.5),vec2(1.0,1.0));
    float searchBarMask = roundBoxMask(searchBarUV,vec2(0.1),0.005);
    vec2 searchIconUV = uvToRect(searchBarUV,vec2(0.1),vec2(0.2,0.9));
    searchBarMask -= max(searchIconMask(searchIconUV),0.0);
    searchBarMask *= S(pihs,pihs+0.01,uv.x);
    vec3 searchBarColor = vec3(0.2,0.2,0.2);
    vec3 searchBar = searchBarColor*searchBarMask;

    bgMask -= searchBarMask;

    // Down panel
    vec2 downPanelUV = uvToRect(uv,vec2(0.25,0.0),vec2(0.75,0.5));
    vec2 leftUV = uvToRect(downPanelUV,vec2(0.0),vec2(0.5,1.0));
    vec3 downPanel = vec3(0.0);
    downPanel += rectMask(leftUV,vec2(0.25,0.42),vec2(0.75,0.58))*TEXT_COLOR;
    downPanel += rectMask(leftUV,vec2(0.0,0.0),vec2(1.0,0.06))*HIGHLIGHT_COLOR;
    vec2 rightUV = uvToRect(downPanelUV,vec2(0.5,0.0),vec2(1.0,1.0));
    downPanel += rectMask(rightUV,vec2(0.25,0.42),vec2(0.75,0.58))*TEXT_COLOR;

    // Background
    bgMask = max(bgMask,0.0);
    float inHeader = inRect(uv,vec2(0.0),vec2(1.0));

    return profileIcon + searchBar + LIGHT_BG*bgMask*inHeader + downPanel;

}

vec3 drawRedditFooter(vec2 uv)
{
    vec3 iconColor = vec3(0.65,0.65,0.65);
    vec3 footerColor = vec3(0.0);
    float bgMask = 1.0;

    // Reddit icon
    vec2 iconUV = uvToRect(uv,vec2(0.0,0.0),vec2(0.2,1.0));
    footerColor += redditIconMask(iconUV)*TEXT_COLOR;

    iconUV = uvToRect(uv,vec2(0.2,0.0),vec2(0.4,1.0));
    footerColor += communityIconMask(iconUV)*TEXT_COLOR*0.4;

    iconUV = uvToRect(uv,vec2(0.4,0.0),vec2(0.6,1.0));
    footerColor += pencilIconMask(iconUV)*TEXT_COLOR*0.4;
    float d = length(iconUV-vec2(0.5,0.5));
    float highlightMask = S(0.4,0.35,d);
    highlightMask = max(0.0,highlightMask-S(0.35,0.3,d));
    footerColor += highlightMask*HIGHLIGHT_COLOR;

    iconUV = uvToRect(uv,vec2(0.6,0.0),vec2(0.8,1.0));
    footerColor += chatIconMask(iconUV)*TEXT_COLOR*0.4;

    iconUV = uvToRect(uv,vec2(0.8,0.0),vec2(1.0,1.0));
    footerColor += inboxIconMask(iconUV)*TEXT_COLOR*0.4;

    bgMask = 1.0-min(min(footerColor.r,footerColor.g),footerColor.b);
    bgMask *= inUVRange(uv);
    return footerColor + bgMask*LIGHT_BG;
}

vec3 drawRedditCenter(vec2 uv , float t)
{
    vec2 suv = uv;
    float inCenterRange = inUVRange(uv);
    uv.y -= 0.2*max(t + (1.0+sin(t)),t);
    uv.y *= 3.0;
    float postID = floor(uv).y;
    float rnd = rand(postID);
    uv = fract(uv); 

    vec2 postUV = uvToRect(uv,vec2(0.0,0.1),vec2(1.0,0.9));
    float inPostRange = inUVRange(postUV);

    // Post header
    vec3 headerColor = vec3(0.0);

    vec2 headerUV = uvToRect(postUV,vec2(0.0,0.7),vec2(1.0,1.0));
    vec2 profileIconUV = uvToRect(headerUV,vec2(0.05,0.0),vec2(0.15,1.0));
    float profileIconMask = S(0.35,0.3,length(profileIconUV-vec2(0.5,0.5)));
    vec3 profileIconColor = hsv2rgb(vec3(rnd,0.7,0.6));
    headerColor += profileIconMask*profileIconColor;

    vec2 titleUV = uvToRect(headerUV,vec2(0.15,0.0),vec2(0.9,1.0));
    vec2 redditPageTitleUV = uvToRect(titleUV,vec2(0.0,0.4),vec2(1.0,1.0));
    headerColor += rectMask(redditPageTitleUV,vec2(0.1,0.3),vec2(0.5+rand(postID)*0.2,0.6))*TEXT_COLOR;

    vec2 postedByUV = uvToRect(titleUV,vec2(0.0,0.0),vec2(1.0,0.4));
    headerColor += rectMask(postedByUV,vec2(0.1,0.3),vec2(0.5+rand(fract(postID*10.0))*0.2,0.6))*LIGHT_TEXT_COLOR;

    vec2 optionsUV = uvToRect(headerUV,vec2(0.9,0.0),vec2(1.0,0.8));
    headerColor += S(0.06,0.05,length(optionsUV-vec2(0.0,0.1)-vec2(0.5)))*LIGHT_TEXT_COLOR;
    headerColor += S(0.06,0.05,length(optionsUV-vec2(0.0,0.3)-vec2(0.5)))*LIGHT_TEXT_COLOR;
    headerColor += S(0.06,0.05,length(optionsUV-vec2(0.0,0.5)-vec2(0.5)))*LIGHT_TEXT_COLOR;

    // Post title
    vec3 titleColor = vec3(0.0);
    vec2 postTitleUV = uvToRect(postUV,vec2(0.0,0.3),vec2(1.0,0.7));
    float postTitleMask = inUVRange(postTitleUV);
    postTitleUV.y *= 2.0;
    float titleLine = floor(postTitleUV).y;
    postTitleUV = fract(postTitleUV);
    titleColor += postTitleMask*rectMask(postTitleUV,vec2(0.05,0.3),vec2(0.5 + 0.5*rand(postID+titleLine),0.6))*TEXT_COLOR;

    // Post footer
    vec3 footerColor = vec3(0.0);
    vec2 postFooterUV = uvToRect(postUV,vec2(0.0,0.0),vec2(1.0,0.3));

    // Todo : make animation of upvote
    vec3 voteupColor = fract(rnd*10.0) < 0.2 ? VOTEUP_COLOR : LIGHT_TEXT_COLOR;
    vec2 voteUV = uvToRect(postFooterUV,vec2(0.05,0.05),vec2(0.3,0.95));
    vec2 voteupUV = uvToRect(voteUV,vec2(0.0,0.0),vec2(0.3,1.0));
    footerColor += upvoteIconMask(voteupUV)*voteupColor;
    vec2 voteCountUV = uvToRect(voteUV,vec2(0.3,0.0),vec2(0.7,1.0));
    footerColor += rectMask(voteCountUV,vec2(0.1,0.4),vec2(0.9,0.6))*voteupColor;
    vec2 votedownUV = uvToRect(voteUV,vec2(0.7,0.0),vec2(1.0,1.0));
    votedownUV = rotate(votedownUV,PI);
    footerColor += upvoteIconMask(votedownUV)*LIGHT_TEXT_COLOR;

    vec2 commentUV = uvToRect(postFooterUV, vec2(0.55,0.05), vec2(0.75,0.95));
    vec2 commentIconUV = uvToRect(commentUV, vec2(0.0,0.0), vec2(0.4,1.0));
    footerColor += commentsIconMask(commentIconUV)*LIGHT_TEXT_COLOR;
    vec2 commentNumberUV = uvToRect(commentUV, vec2(0.4,0.0), vec2(1.0,1.0));
    footerColor += rectMask(commentNumberUV,vec2(0.1,0.4),vec2(0.9,0.6))*LIGHT_TEXT_COLOR;

    vec3 finalColor = headerColor + titleColor + footerColor;
    float bgMask = 1.0-min(min(finalColor.r,finalColor.g),finalColor.b);

    return inCenterRange*inPostRange* (finalColor + bgMask*LIGHT_BG);
}

vec3 drawScreen(vec2 uv, float t)
{
    float hss = 0.15; // header screen size
    float fss = 0.1; // footer screen size

    vec2 headerUV = uvToRect(uv,vec2(0.0,1.0-hss),vec2(1.0,1.0));
    vec2 footerUV = uvToRect(uv, vec2(0.0,0.0),vec2(1.0,fss));
    vec2 centerUV = uvToRect(uv, vec2(0.0,fss),vec2(1.0,1.0-hss));

    float transitionMask = S(fss,fss+0.05,uv.y);
    transitionMask = min(S(1.0-hss,1.0-hss-0.05,uv.y),transitionMask);

    return drawRedditHeader(headerUV) + drawRedditFooter(footerUV) + transitionMask*drawRedditCenter(centerUV,t);
}

vec3 drawPhone(vec2 uv, float t)
{
    vec2 screenUV = uvToRect(uv,vec2(0.05,0.05),vec2(0.95,0.9));
    vec3 screenColor = drawScreen(screenUV,t);
    float screenMask = 1.0;
    screenMask *= S(1.0,0.96,screenUV.x);
    screenMask *= S(0.0,0.04,screenUV.x);
    screenMask *= S(0.0,0.01,screenUV.y);
    screenMask *= S(1.0,0.99,screenUV.y);

    vec3 phoneColor = hsv2rgb(vec3(rand(iDate.x),0.6,0.8));
    float phoneMask = 1.0-inUVRange(screenUV);
    phoneMask *= inUVRange(uv);
    phoneMask *= S(1.0,0.96,uv.x);
    phoneMask *= S(0.0,0.04,uv.x);
    phoneMask *= S(0.0,0.01,uv.y);
    phoneMask *= S(1.0,0.99,uv.y);

    vec2 phoneHeaderUV = uvToRect(uv,vec2(0.05,0.92),vec2(0.95,0.98));
    vec2 speakerUV = uvToRect(phoneHeaderUV,vec2(0.6,0.0), vec2(0.9,1.0));
    float speakerMask = inUVRange(speakerUV);
    speakerUV *= vec2(8.0,3.0);
    speakerUV = fract(speakerUV);
    phoneColor -= speakerMask*S(0.33,0.3,length(speakerUV-vec2(0.5)))*vec3(1.0);

    vec2 cameraUV = uvToRect(phoneHeaderUV,vec2(0.05,0.0), vec2(0.25,1.0));
    float cameraMask = inUVRange(cameraUV);
    cameraUV.x *= 2.0;
    cameraUV = fract(cameraUV);
    phoneColor -= cameraMask*S(0.38,0.35,length(cameraUV-vec2(0.5)))*vec3(1.0);

    vec3 finalColor = screenMask*screenColor + phoneMask*phoneColor;

    return finalColor;
}

