vec3 drawBackground(vec2 fragCoord, float t)
{
    float rotX = 3.14+0.5*sin(0.1*t);
    float rotY = 1.57;
    vec2 uv = 2.5 * (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.xx;
    vec3 camO = vec3(cos(rotX), cos(rotY), sin(rotX));
    vec3 camD = normalize(vec3(0)-camO);
    vec3 camR = normalize(cross(camD, vec3(0, 1, 0)));
    vec3 camU = cross(camR,camD);
   	vec3 dir =  normalize(uv.x * camR + uv.y * camU + camD);
    return texture(iChannel0, dir).rgb;
}

vec4 mn( vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 phoneUV = uvToRect(uv,vec2(0.25,0.1), vec2(0.53,0.9));
    vec3 phoneColor = drawPhone(phoneUV,iTime);
    float phoneMask = inUVRange(phoneUV);

    vec3 backgroundColor = drawBackground(fragCoord,iTime);

    vec3 col = phoneColor + (1.0-phoneMask)*backgroundColor;

