                  // --- pull original parameterization falling in this pixel
    float l = length(U), c, l0=l;
    U /= l;
    // d' = f(d)        f(x) = (x^n+D^n)^(1/n)  
    // d = f^-1(d')  f^-1(x) = (x^n-D^n)^(1/n)
    l *= c = pow( abs(1.-pow(D/l,n)), 1./n );   // c = concentration
    U *= l; 

                  // --- apply
    vec4 T = 2.*texture(iChannel0, .5+.5*U*8.*R.y/R);

    U *= 8.;     // draw grid parameterization
    O = vec4( max( S(U.x) , S(U.y) ) , 0,0,0);

    O += T;

  //O *= max(0.,1.-1.*c); // green: concentration
    O *= smoothstep( 1.5, 0., abs(l0-D)*6. );

