// This function checks a rough area around the light source to see if any partly obscures it,
// reducing the lense flare accordingly, or let's say, guessingly!

float obscurePartsOfLight(vec2 p)
{
    float a = 0.0, z;
    float e = .03;
    float d = length(sunPos - camera);
    vec2 asp = vec2(iResolution.y/iResolution.x,1.0);
   	vec2 texUV = .5+.5*p*asp;
   	z = texture(iChannel0, texUV).w;
    if (z > d) a +=.5;

    texUV = .5+.5*(p+vec2(e, e))*asp;
   	z = texture(iChannel0, texUV).w;
    if (z > d) a +=.125;

    texUV = .5+.5*(p+vec2(e, -e))*asp;
   	z = texture(iChannel0, texUV).w;
    if (z > d) a +=.125;

    texUV = .5+.5*(p+vec2(-e, -e))*asp;
   	z = texture(iChannel0, texUV).w;
    if (z > d) a +=.125;

    texUV = .5+.5*(p+vec2(-e, e))*asp;
   	z = texture(iChannel0, texUV).w;
    if (z > d) a +=.125;

    return a;
}    


//---------------------------------------------------------------------------------
vec3 lenseFlare(vec2 uv,vec3 dir)
{

    vec3 col = vec3(0);
    sunLight = normalize(sunPos - camera);

    mat3 inv = transpose(camMat);
    vec3 cp = inv * (camera - sunPos);

    if (cp.z < 0.0)
    {
    	vec2 sun2d = zProj * cp.xy / cp.z;
         if (sun2d.x < -1.0 || sun2d.x > 1. || sun2d.y < -1.0 || sun2d.y > 1.) return col;

        float z = obscurePartsOfLight(sun2d);
        if (z > 0.0)
        {
            vec2 uvT = uv - sun2d;
            uvT = uvT*(length(uvT));
            float bri = max(dot(dir, sunLight)*.2, 0.0);
            bri = pow(bri, 3.)*5.*z;


            // glare1
            float glare1 = max(dot(dir,sunLight),0.0);
            // glare2
            float glare2 = max(1.7-length(uvT+sun2d*1.8)*2.0, 0.0);
            //uvT = mix (uvT, uv, -2.3);
            // glare3
			float glare3 = max(1.-pow(length(uvT+sun2d*4.)*3., 2.), 0.0);

            col += bri * vec3(.5, .5, .5)  * pow(glare1, 18.5)*3.5;
            col += bri * vec3(.5, .5, .2) * pow(glare2, 2.0)*5.;
          	col += bri * vec3(.2,.5, .5) * pow(glare3, 4.0)*5.0;
        }
	}
    return max(vec3(0.0),col);
}
// https://lime-limelemon.blogspot.com/2016/03/aces-filmic-tone-mapping-curve.html
vec3 acesFilm(const vec3 x) {
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return clamp((x * (a * x + b)) / (x * (c * x + d ) + e), 0.0, 1.0);
    //return -0.053173 + x*(2.170622 + x*(-2.132296 + x*(0.996822 + x*-0.176583)));
}

