void mainImage( out vec4 colOut, in vec2 fragCoord )
{

    float mouseX = iMouse.x / iResolution.x;
    vec2 uv = (-iResolution.xy + 2.0 * fragCoord ) / iResolution.y;


           // Setup camera...
	time = getTime(iTime, mouseX);
     // Setup camera...
    camera = getCamPos(time);
	vec3 lookat = getTarPos(time);
    camMat = getCamMat(camera, lookat, 0.);
    zProj = projectZ(uv);
    vec3 setDir = normalize( vec3(uv, zProj));
	vec3 rd = camMat * setDir;
	sunPos = getLightPos(time);


    // Init output...
    vec3 col = texelFetch(iChannel0, ivec2(fragCoord), 0).xyz;
    col += lenseFlare(uv, rd);

    col = clamp(col, 0.0, 1.0);
    col = acesFilm(col);// Filmic filter AND some over-kill contrast. Yeah!!!! :)
	col = col*col*(3.0-2.0*col);

	// Finally some border vignette...
    uv = fragCoord / iResolution.xy;
    col *= .5+ .5*pow(100.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), 0.6 );	
    col *= smoothstep(0.0, 4.0, iTime);
    #ifdef OFF_LINE
    col *= smoothstep(374.0, 360.0, iTime);
    #endif

    colOut = vec4(sqrt(col), 1.0);
}

