// Quantum Weirdness
// by Dave Hoskins. August 2020
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.


mat2 rotm;
float pix;
float mist;

//--------------------------------------------------------------------------------
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    d+=fract(time*.2);
    return a + b*cos( 6.28318*(c*t+d) );
}

//--------------------------------------------------------------------------------
vec3 texCube(sampler2D sam, in vec3 p, in vec3 n )
{
	vec3 x = texture(sam, p.yz).xyz;
	vec3 y = texture(sam, p.zx).xyz;
	vec3 z = texture(sam, p.xy).xyz;
	return (x*abs(n.x) + y*abs(n.y) + z*abs(n.z))/(abs(n.x)+abs(n.y)+abs(n.z));
}

//--------------------------------------------------------------------------------
mat2 rot2D(float a) {
    a=radians(a);
	float s=sin(a);
    float c=cos(a);
    return mat2(c,s,-s,c);
}

//--------------------------------------------------------------------------------
// With thanks to Kali...
// https://www.shadertoy.com/view/WtfXzf
vec2 de(vec3 p)
{
    p=abs(15.-mod(p+15.,30.));
    float md=200.;
    float s=1.2;
    float sc=1., id = 0.;
    vec3 pc;

    float turn  = sin(time*1.3)*2.3;

    float rot=sin(time*.3)*220.;
    for (float i = 0.0; i < 1.0; i+= 1.0/4.0)
    {
      	p.xy=abs(p.xy); 
        p=p*s-4./sc;
        sc*=s;
        p.xz*=rotm;
        p.yz*=rot2D(2.+rot);
        float d=length(p.xz+sin(p.y*turn));
        if (d<md)
        {
        	md=d;
			pc=p;
            id = i;
        }
    }
    float l=max(0.2,(pc.y*.01));
    return vec2((md/sc)*l, id);
}

//--------------------------------------------------------------------------------
float shadow( in vec3 ro, in vec3 rd)
{
	float res = 1.0;
    float t = .1;
	float h;

    for (int i = 0; i < 15; i++)
	{
        vec3 p =  ro + rd*t;

		h = de(p).x;
		res = min(6.*h / t, res);
		t += h;
	}
    //res += t*t*.08; // Dim over distance
    return clamp(res, .1, 1.0);
}

//--------------------------------------------------------------------------------
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

//--------------------------------------------------------------------------------
vec2 rayMarch(vec3 ro, vec3 rd, vec2 uv)
{
    float l = hash12(uv)*.5;
    int i;
    vec2 d2;
    mist = 0.0;

    #ifdef OFF_LINE
    for (i = 0; i < 800; i++)
    #else
    for (i = 0; i < 300; i++)
    #endif
    {
        vec3 p = ro + rd * l;
        d2 = de(p);
        mist += smoothstep(.12,.03,d2.x);

        if (abs(d2.x) < .03 || l > 1000.0) break;
        #ifdef OFF_LINE
        l += d2.x*.5;
        #else
        l += d2.x*.85;
        #endif
	}
    mist = pow(mist, 3.) * .0001;
    return vec2(l, d2.y);
}


//--------------------------------------------------------------------------------
vec3 lighting(vec3 p, vec3 nor, vec3 rd, vec3 mat)
{
    sunLight = normalize(sunPos - p);
    float sh = shadow(p+sunLight*0.2,  sunLight);
    float c = max(dot(nor, sunLight), 0.0)+abs(nor.y)*sh*.5;
    vec3 ref = reflect(rd, nor);

    vec3 spec = texture(iChannel0, ref).xyz;
    vec3 col = ((mat * c)+ pow(spec, vec3(3.0))*.2)*sh;
     #ifndef OFF_LINE
    col+= mist * vec3((cos(time*1.51)+.5)*.1, 0.01, (sin(time*1.37)+.5)*0.1)  * sh;
    #else
    col+= mist * vec3((cos(time*1.51)+.5)*.1, 0.01, (sin(time*1.37)+.5)*0.1) * .3 * sh;
    #endif
    return col;

}


//--------------------------------------------------------------------------------
vec3 normal(vec3 p, float ds)
{
    ds *= ds;
	float e = min(.4, pix * ds);

    return normalize( vec3( de(p+vec3(e,0.0,0.0)).x - de(p-vec3(e,0.0,0.0)).x,
                            de(p+vec3(0.0,e,0.0)).x - de(p-vec3(0.0,e,0.0)).x,
                            de(p+vec3(0.0,0.0,e)).x - de(p-vec3(0.0,0.0,e)).x));
}

//----------------------------------------------------------------------------------------
vec3 lightSource(vec3 spotLight, vec3 dir, float dis)
{
    float g = 0.0;
    if (length(spotLight) < dis)
    {
		g = pow(max(dot(normalize(spotLight), dir), 0.0), 2000.0);
    }

    return vec3(g, g*.8, g*.7)*.8;
}


//--------------------------------------------------------------------------------
void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    // Normalise coordinates to whatever resolution / ratio we have...
    float mouseX = iMouse.x / iResolution.x;
    vec2 uv = (-iResolution.xy + 2.0 * fragCoord ) / iResolution.y;
    pix = .1/iResolution.y;

    rotm=rot2D(-30.);

    /// Init output...
    vec3 col = vec3(0.);


