    time = getTime(iTime, mouseX);
    camera = getCamPos(time);
	vec3 lookat = getTarPos(time);

    camMat = getCamMat(camera, lookat, 0.);
    zProj = projectZ(uv);

    vec3 setDir = normalize( vec3(uv, zProj));
	vec3 rd = camMat * setDir;

    //mat3 camMat = getCamMat(uv, camera, lookat,0.0, rd);
    sunPos = getLightPos(time);

    vec2 dis = rayMarch(camera, rd, fragCoord);
    if (dis.x < 1000.0)
    {
        vec3 p  = camera + rd * dis.x;
        vec3 nor = normal(p, dis.x);
        vec3 matA =  pal( dis.y*1.8, vec3(0.4,0.4,0.3),vec3(.4,.2,.2),vec3(.4, .9, .5),vec3(0.4, 0.1, 1.0) );
        vec3 matB =  pal( dis.x, vec3(0.4,0.4,0.3),vec3(.4,.2,.2),vec3(.4, .9, .5),vec3(0.4, 0.1, 1.0) );
        matA = mix(matA, matB, .5);
        col = lighting(p, nor, rd, matA);


