#define MAX_DIST 100.0
#define MAX_STEP 100.0
#define SURF_DIST 0.01		// 이 범위 안이면 더 이상 할 필요 없이 충돌한 것으로 인정하기

float GetDist( vec3 p ) {

    vec4 sphere = vec4( 0 , 1.5 , 6 , 1 ); // XYZ + scale

    float sphereDist = length( p - sphere.xyz ) - sphere.w;

    float planDist = p.y;

	float minDist = min ( planDist , sphereDist );

    return minDist;
}


float RayMarch( vec3 ro , vec3 rd ) {
	float dO = 0.0;

    for ( float i = 0.0 ; i < MAX_STEP ; i++ ) {

        vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += dS;

        if ( dO > MAX_DIST || dS < SURF_DIST ) break;
    }


    // 거리를 리턴한다.
    return dO;
}


vec3 GetNormal( vec3 p ) {
    float d = GetDist(p);

    vec2 e = vec2( 0.01, 0.0 );
    vec3 n = d - vec3(
        	GetDist( p - e.xyy ),
        	GetDist( p - e.yxy ),
        	GetDist( p - e.yyx )
        );

    return normalize( n );
}



float GetLight( vec3 p ) {
    vec3 lightPos = vec3( 0, 5, 6 );
    lightPos.zx += vec2( sin(iTime) , cos(iTime)  ) * 2.0;

    vec3 l = normalize( lightPos - p );

    vec3 n = GetNormal(p);

    float diffuse = clamp( dot ( n , l ) , 0.0 , 1.0 ) ;	// same as 1 * 1 * cos(t)

    // Shdow
    float d = RayMarch( p + n * SURF_DIST * 2.0 , l );
    if ( d < length( lightPos - p )) diffuse *= 0.25;


    return diffuse;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy ) /iResolution.y;
	vec3 col = vec3(0);

    vec3 ro = vec3( 0 , 1 , 0 );
    vec3 rd = normalize( vec3( uv.x , uv.y , 1) );

    float d = RayMarch( ro , rd );
    vec3 intersectionPoint = ro + rd * d;


    float diffuse = GetLight( intersectionPoint );

    col = vec3(diffuse);


    fragColor = vec4(col,1.0);
}

