#define s sin(iTime)
#define c cos(iTime)
#define dist 4.0

float sd(vec3 pos, float d) {
    vec3 p = pos - vec3(0, 0, d);
    float py = p.y;
    p.y = p.y * c - p.z * s;
    p.z = p.z * c + py * s;
    vec2 t = vec2(1.0, .4);
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 ambient = vec3(0.4, 0.7, 1.0) / 5.0;
    vec3 light = normalize(vec3(0.8, 0.5, -0.5));
    vec2 uv = (fragCoord - iResolution.xy / 2.0) / iResolution.x;
    int iters = 0;
    float raylength = 0.0;
    vec3 incoming = normalize(vec3(uv, 1));
    float SDE = 1.0;
    vec3 z = vec3(0.0);

    while(iters < 5000 && abs(raylength) < 100.0 && abs(SDE) > 0.00001) {
        z = raylength * incoming;
        SDE = sd(z, dist);
        raylength += SDE;
        iters++;
    }

    vec3 col = ambient;
    if(abs(SDE) <= 0.00001) {
        vec3 inVec = incoming;
        vec3 z1 = z;
        raylength = 0.0;
        incoming = normalize(vec3(uv.x + 0.0001, uv.y, 1));
        SDE = 1.0;
        z = vec3(0.0);
        iters = 0;

        while(iters < 5000 && abs(raylength) < 100.0 && abs(SDE) > 0.00001) {
            z = raylength * incoming;
            SDE = sd(z, dist);
            raylength += SDE;
            iters++;
        }

        vec3 z2 = z;
        raylength = 0.0;
        incoming = normalize(vec3(uv.x, uv.y + 0.0001, 1));
        SDE = 1.0;
        iters = 0;
        while(iters < 5000 && abs(raylength) < 100.0 && abs(SDE) > 0.00001) {
            z = raylength * incoming;
            SDE = sd(z, dist);
            raylength += SDE;
            iters++;
        }

        vec3 z3 = z;
        raylength = 0.0001;
        incoming = light;
        SDE = 1.0;
        z = vec3(0.0);
        iters = 0;
        while(iters < 5000 && abs(raylength) < 100.0 && abs(SDE) > 0.00001) {
            z = raylength * incoming;
            SDE = sd(z + z1, dist);
            raylength += SDE;
            iters++;
        }

        vec3 a = z3 - z1;
        vec3 b = z2 - z1;
        vec3 color = vec3(1.5, 1.0, 0.5);
        vec3 normal = normalize(vec3(a.y * b.z - a.z * b.y, a.z * b.x - a.x * b.z, a.x * b.y - a.y * b.x));
        vec3 reflection = normal * 2.0 * dot(normal, inVec) - inVec;
        raylength = 0.01;
        incoming = -reflection;
        SDE = 1.0;
        z = vec3(0.0);
        iters = 0;
        while(iters < 5000 && abs(raylength) < 100.0 && abs(SDE) > 0.00001) {
            z = raylength * incoming;
            SDE = sd(z + z1, dist);
            raylength += SDE;
            iters++;
        }

        vec3 diffuse = (vec3(max(dot(normal, light) * 5.0, 0.0) * color) + ambient) * 0.1;
        vec3 specular = pow(max(dot(normalize(light - inVec), normal), 0.0), 200.1) * 9.0 + ambient;
        if(abs(SDE) <= 0.00001) {
            specular = ((1.0 * color) + ambient) * 0.1;
        }
        float fresnel = 0.05 + 0.95 * pow(1.0 + dot(inVec, normal), 5.0);
        col = diffuse * (1.0 - fresnel) + specular * fresnel;
    }

    fragColor = vec4(pow(col.x, 0.4545), pow(col.y, 0.4545), pow(col.z, 0.4545), 1.0);
}

