/* Shader properties */

vec4 transp = vec4(0.0,0.0,0.0,0.0);
vec4 color1 = vec4 (0.867, 0.675, 0.557, 1.0);
vec4 color2 = vec4 (0.871, 0.843, 0.804, 1.0);

float w = 0.008; // width of grid (UV coords)
float shrinkSpeed = 0.7; // shrinking
float lineSpeed = 0.2; // line scan
float fadeSpeed = 1.0; // color fade

float time = 0.0; // animation time

/* Random */

#define SEED 987654.321
// Generates a random float from another 2D vector
// seed must be a large number
// output range: [0..1[
float randFloat(in vec2 vec, in float seed)
{
    return fract(sin(vec.x*99.9+vec.y)*seed);
}

/* DisintegrateToCircles Shader

	This shader uses a pattern from "A Handbook of Ornament", by Franz Sales Meyer (page 21, pattern 2).
	After filling the original 90° arcs for each block of the grid, the shader completes each circle
	by drawing arcs from the opposite end of the block.
	The radiuses are shrunk in random rates, calculated by the original semicircle position on grid.
	The color is faded from the original color to two different colors, which adds depth to the pattern,
	and a nice "glow" effect on the border of the disintegration.
*/


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // To visualize the animation in Shadertoy, the time is updated to global time
    // When using the shader, the time property should be animated extternally
    time = iTime;

    // Calculate UV and get input color
    vec2 uv = fragCoord/iResolution.xy;
    vec4 img = texture(iChannel0, uv.xy);

    // Calculate height from aspect, for square grid
    float h = w * (iResolution.x/iResolution.y);

    // Scale grid so that each square goes from 0..1   
    uv.x /= w;
    uv.y /= h;

    // Offset rows 3 and 4
    if (int(uv.y)%4>1) uv.x += 2.0;

    // Calculate local coordinates in the block
    // And grid coordinates
    vec2 local = fract(uv);
    int xg = int(uv.x);
    int yg = int(uv.y);

    // Line sweep position
    float line = time * lineSpeed * (1.0/h);

    // Draw
    vec4 color = vec4(0.0,0.0,0.0,1.0);
    // Above the line, just draw the image
    if (yg/2 > int(line/2.0)) {
        color = img;
    }
    // Below the line, draw pattern
    else {
        // Flip local coordinates according to pattern
        int xp = xg%4;
        int yp = yg%2;
        if (yp == 0) {
            if (xp == 0 || xp == 1) local.y = 1.0 - local.y;
            if (xp == 1 || xp == 2) local.x = 1.0 - local.x;
        }
        else if (yp == 1) {
            if (xp == 1 || xp == 2) local.x = 1.0 - local.x;
            if (xp == 2 || xp == 3) local.y = 1.0 - local.y;
        }

        // Get random scale for each main semi-circle
        float rscale = 0.0;
        if (xp < 2) rscale = randFloat(vec2(xg,yg/2), SEED);
        else rscale = randFloat(vec2(xg/2,yg), SEED);

        // Get random scale of neighbour semi-circle
        float nrscale = 0.0;
        if (xp < 2) {
            if (yp == 1) {
                if ((yg/2)%2 == 0) nrscale = randFloat(vec2(xg/2+1,yg+1), SEED);
                else nrscale = randFloat(vec2(xg/2-1,yg+1), SEED);
            }
            else {
                if ((yg/2)%2 == 0) nrscale = randFloat(vec2(xg/2+1,yg-1), SEED);
                else nrscale = randFloat(vec2(xg/2-1,yg-1), SEED);
            }
        }
        else {
            if (xp == 2) nrscale = randFloat(vec2(xg-1,yg/2), SEED);
            else nrscale = randFloat(vec2(xg+1,yg/2), SEED);
        }

        // Clip scales to range [0,0.5[
        rscale = rscale/2.0+0.5;
        nrscale = nrscale/2.0+0.5;

        // Calculate distance and neighbour distance to center
        float dist = length(local);
        float ndist = length(1.0-local);

        // Animate radius
        float radius = 1.0 - time * rscale * shrinkSpeed;
        float nradius = 1.0 - time * nrscale * shrinkSpeed;

        // Time offset for y position
        float t = ((uv.y*h)/lineSpeed);
        radius += t*rscale*shrinkSpeed;
        nradius += t*nrscale*shrinkSpeed;

        // Color fade time
        if (time > t) t = (time-t)*fadeSpeed;
        else t = 0.0;

        // Draw circles
        if (xp < 2) {
            if (dist <= radius) color += mix(img, color1, t);
            else if (ndist <= nradius) color += mix(img, color2, t);
        }
        else {
            if (dist <= radius) color += mix(img, color2, t);
            else if (ndist <= nradius) color += mix(img, color1, t);
        }
    }

    fragColor = color;
}

