float sdSphere (in vec3 p, in vec3 c, in float r) {
    return r - length(p - c);
}

float sdBox( vec3 p, vec3 b ) {
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec2 map (in vec3 p) {
    vec2 v = vec2(1.0 - sdBox(p, vec3(1.0)), 1.0);
    return v;
}

const vec3 xDir = vec3(0.00001, 0, 0);
const vec3 yDir = vec3(0, 0.00001, 0);
const vec3 zDir = vec3(0, 0, 0.00001);
vec3 surfaceNormal (in vec3 pos) {
    vec3 normal = vec3(
        map(pos + xDir).x - map(pos - xDir).x,
        map(pos + yDir).x - map(pos - yDir).x,
        map(pos + zDir).x - map(pos - zDir).x
    );
    return normalize(normal);
}

vec3 ld = vec3(1.0, 0.0, 0.0);

vec3 trace (in vec3 ro, in vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < MAXSTEPS; i ++) {
    	vec3 pos = ro + t * rd;
        vec2 dist = map(pos);
        if (dist.x < EPSILON) {
        	vec3 no = surfaceNormal(pos);

            float diffuse = max(-dot(no, ld), 0.0);
            float specular = max(-dot(reflect(rd, no), ld), 0.0);
            specular = pow(specular, 32.0);

            //float shade = diffuse * 0.7 + specular * 0.3;
            float shade = 1.0;
            vec3 tint = texture(iChannel0, pos).rgb;

            vec3 an = abs(no);
            if (an.x > an.y && an.x > an.z) {
           		tint = texture(iChannel1, pos).rgb;
            } else if (an.y > an.x && an.y > an.z) {
            	tint = texture(iChannel2, pos).rgb;
            }
            return shade * tint;

        }
        t += dist.x;
    }
    return vec3(0.0);
}

