vec2 polarToDecart(vec2 polar) {
    float alpha = polar.x;
    float R = polar.y;
    float x = sin(alpha) * R;
    float y = cos(alpha) * R;
    return vec2(x, y);
}

vec2 decartToPolar(vec2 decart) {
    float alpha = atan(decart.x, decart.y);
    float R = length(decart);
    return vec2(alpha, R);
}


float Menger(vec3 p)
{
    int n = 0;
    float scale = 0.33 + .3 * smoothstep(0., 1., BEAT2);//0.433333;
    int iterations = int((BEAT0 + 0.2) * 5.);
    p.xz *= Rot(/* -2. * PI * iMouse.x / iResolution.x +  */-BEAT0);
    p.xy *= Rot(/*-2. * PI * iMouse.y / iResolution.y*/-BEAT1);
   while (n < iterations) {
        p /= scale;
    p.xy *= Rot(/*-2. * PI * iMouse.y / iResolution.y*/BEAT1);
    p.xz *= Rot(/* -2. * PI * iMouse.x / iResolution.x +  */BEAT0);
        p.z = -abs(p.z);
        p.y = abs(p.y);
        p.x = abs(p.x);
        p -= (vec3(2., 2., -2.));
        if(p.x+p.z<0.) p.xz = -p.zx;
        if(p.y+p.z>0.) p.yz = -p.zy;
        p.y += 2.;
        p.y = abs(p.y);
        p.y -= 1.;
        p.y = abs(p.y);
        p.y -= 1.;
        n++;
    }
    return sdBox(p, vec3(1)) * pow(scale, float(iterations));
}

float sdColumn(vec3 p, float width) {
	//p.y = mod(p.y, 1.);
    float fractal = Menger(p);
    return fractal;
}

