//amount of particles
//const int PARTICLES = 1000; 
#define PARTICLES int(R.x*R.y/800.) // 230 2300

#define R iResolution.xy
#define iResolution1 (R/4.)  // slot allocated for Particle Buffer 
#define iResolution2 (R/4.)  // slot allocated for Voronoi Buffer
#define ofs2     vec2(iResolution1.x,0)

// --- chose length of closest LIST
//#define LIST xxxx    //  1 (not enough for repulsion & draw voronoi)
//#define LIST xyyy    //  2
//#define LIST xyzz    //  3
  #define LIST xyzw    //  4

// --- Particle Buffer part:
// in this buffer every pixel represents a particle
// the particles positions is stored in .xy
//           its velocity  is stored in .zw
// Only the first PARTICLES amount of pixels are actually used.

// --- Voronoi Buffer part:
// every pixel stores the 4 closest particles to it
// every frame this data is shared between neighbours


//returns the ids of the four closest particles from the input
//    if (xy != ivec2(clamp(vec2(xy),vec2(0),iResolution2)) ) return ivec4(0);
#define getClosest(xy) /* xy: location within Voronoi Buffer */ \
    ivec4(texelFetch(iChannel0, ivec2(ofs2+vec2(xy)), 0).LIST) // was channel1

#define getClosestS(pos) getClosest(ivec2(vec2(pos)/R *iResolution2))

//returns the location of the particle within the particle buffer corresponding with the input id 
#define locFromID(id) ivec2( (id) % int(iResolution1.x), (id) / int(iResolution1.x))

//get the particle corresponding to the input id
#define getParticle(id) texelFetch(iChannel0, locFromID(id), 0)



//hashing noise by IQ
float hash( int k ) {
    uint n = uint(k);
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.0;
}

#define Nhash(h) hash(int((h)*41343.))

