// Fork of my fork of Gijs' https://shadertoy.com/view/WltSz7 that was rewording mine :-)

float distance2Particle(int id, vec2 W){ // screen coords
    if( id < 0 ) return 1e20;
    vec2 delta = getParticle(id).xy - W;
    return dot( delta, delta );
}

void insertion_sort(inout ivec4 i, inout vec4 d, int i_, float d_){	
    if(any(equal(ivec4(i_),i))) return;
    if     (d_ < d[0])  i = ivec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])  i = ivec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])  i = ivec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])  i = ivec4(i.xyz,i_),    d = vec4(d.xyz,d_);
    i = i.LIST; d = d.LIST;
}

// =========================================================
void mainImage( out vec4 O, vec2 U ){
    ivec2 I = ivec2(U);
    if ( U+.5 == R ) O.xy = R; // store current resol to detect resol change

// === main Particle Buffer part
  vec2 _m = U - iResolution1; 
  if (max(_m.x,_m.y)<0.) {

    //we only simulate PARTICLES amount of particles
    int id = I.x + I.y*int(iResolution1.x);
    if(id >= PARTICLES) return;

    vec4 prev = texelFetch( iChannel0, ivec2(U), 0 );
    vec2  pos = prev.xy,              // screen coords
          vel = prev.zw;              // velocity

    if(texelFetch(iChannel0, ivec2(R)-1,0).xy != R ) { // 1st run or resolution change
        float  x = hash(id),  y = Nhash(x),  // --- pick a "random" starting position
              vx = Nhash(y), vy = Nhash(vx);
        pos = R * vec2( x, y);        // screen coordinates
        vel = 2.* vec2(vx,vy) - 1. ;  // random velocity
    }
    pos += .1 * vel;                  // --- motion
    pos = mod(pos,R);                 // wrap screen

    O = vec4(pos,vel);
}

// === main Voronoi Buffer part
  _m = U - ofs2 - iResolution2; 
  if (U.x >= iResolution1.x && max(_m.x,_m.y) < 0.) {   

    //if(iFrame==0){ fragColor = vec4(0); return; }
    ivec2 I = ivec2(U-ofs2);
    vec2  W = (U-ofs2)/iResolution2 * R;

    ivec4 new = ivec4(-1);  //in this vector the four new closest particles' ids will be stored  
    vec4  dis = vec4(1e20); //in this vector the distance to these particles will be stored 

    //get all known closest particles from old self and neighbours
    ivec4 old   = getClosest( I + ivec2( 0, 0) ),           
          east  = getClosest( I + ivec2( 1, 0) ),
	      north = getClosest( I + ivec2( 0, 1) ),
          west  = getClosest( I + ivec2(-1, 0) ),
          south = getClosest( I + ivec2( 0,-1) );
    //collect them in a array so we can loop over it
    ivec4[5] candidates = ivec4[5](old, east, north, west, south); 

    for(int i=0; i<5; i++)
        for(int j=0; j<4; j++){
            int id = candidates[i][j];
            float dis2 = distance2Particle(id, W); // to screen
            insertion_sort( new, dis, id, dis2 );
        }

    for(int k = 0; k < 1; k++){
        //random hash. We should make sure that two pixels in the same frame never make the same hash!
        float h = hash(
            I.x + 
            I.y  * int(iResolution2.x) + 
            iFrame*int(iResolution2.x*iResolution2.y) +
            k
        );
        //pick random id of particle
        int p = int(h*float(PARTICLES));
        insertion_sort(new, dis, p, distance2Particle(p, W) );
    }

    O = vec4(new);   
  }
}

