// 'Waiting...'
//
// My first attempt at modelling a character.
// Sort of a sad little wonkly wax chap...  With a butt...
//
// Concrete texture reused from my earlier shader:
// https://www.shadertoy.com/view/WllfzB
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq, Shane
// and a bunch of others for sharing their knowledge!

// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define MIN_DIST 0.0015

#define AA  // Comment out this line for a speed boost.

// Thanks Shane - https://www.shadertoy.com/view/lstGRB
float noise(vec3 p) {
	const vec3 s = vec3(7.0, 157.0, 113.0);
	vec3 ip = floor(p);
    vec4 h = vec4(0.0, s.yz, s.y + s.z) + dot(ip, s);
	p -= ip;

    h = mix(fract(sin(h) * 43758.5453), fract(sin(h + s.x) * 43758.5453), p.x);

    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z);
}

// Thanks iq.
float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float fbm(vec3 p) {
    return (texture(iChannel0, p * 0.01).r
    + texture(iChannel0, p * 0.01978).r * 0.4848
    + texture(iChannel0, p * 0.0391).r * 0.235
    + texture(iChannel0, p * 0.07739).r * 0.114
    + texture(iChannel0, p * 0.15307).r * 0.055
    + texture(iChannel0, p * 0.30278).r * 0.027) / 1.9158;
}

mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

vec2 min2(vec2 a, vec2 b) { return a.x < b.x ? a : b; }

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdCappedCylinder(vec3 p, float h, float r) {
    vec2 d = abs(vec2(length(p.xy), p.z)) - vec2(h, r);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdRoundCone(vec3 p, vec3 a, vec3 b, float r1, float r2) {
    // sampling independent computations (only depend on shape)
    vec3  ba = b - a;
    float l2 = dot(ba,ba);
    float rr = r1 - r2;
    float a2 = l2 - rr*rr;
    float il2 = 1.0/l2;

    // sampling dependant computations
    vec3 pa = p - a;
    float y = dot(pa,ba);
    float z = y - l2;
    vec3 f = pa*l2 - ba*y;
    float x2 = dot(f, f);
    float y2 = y*y*l2;
    float z2 = z*z*l2;

    // single square root!
    float k = sign(rr)*rr*rr*x2;
    if( sign(z)*a2*z2 > k ) return  sqrt(x2 + z2)        *il2 - r2;
    if( sign(y)*a2*y2 < k ) return  sqrt(x2 + y2)        *il2 - r1;
                            return (sqrt(x2*a2*il2)+y*rr)*il2 - r1;
}

float getConcreteMaterial(vec3 p, out vec3 mat) {
    // Set rnd seed from id.
    vec3 tp = p;

    // Mix a couple of shades of grey.
    float baseColor = smoothstep(0.0, 0.5, noise(tp));
    mat = mix(vec3(0.18, 0.17, 0.17), vec3(0.20, 0.19, 0.19), baseColor);

    // Surface roughness.
    float rough = noise(tp * 60.0) * 0.005 // Base
         		  + step(0.2, noise(tp * 26.666)) * 0.0033; // Pits/dents.
    mat += rough * 24.0;

    // Fade surface roughness(/deflection) out with distance to prevent screen noise.
    return rough;
}

vec3 getRayDir(vec3 ro, vec3 lookAt, vec2 uv) {
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    return normalize(forward + right * uv.x + up * uv.y);
}

bool isEye = false;
float nod = 0.0;
float head(vec3 p) {
    p.y += 0.9;
    nod = pow(abs(sin(iTime * 0.4) * 0.5 + 0.5), 2.0);
    p.yz *= rot(0.4 * nod - 0.3);
    p.xz *= rot(sin(iTime) * 0.2 * (1.0 - pow(nod, 2.0)));
    p.y -= 0.9;

    float rough = fbm(p);
    float d = length(p) - 1.0 - mix(-0.5, 0.5, rough);

    float eye = length(p + vec3(0.025, 0.3, 0.825)) - rough * 0.4; // Left eye
    eye = min(eye, length(p + vec3(-0.4, 0.2, 0.75)) - rough * 0.5); // Right eye

    isEye = eye < d;
    return min(d, eye);
}

// Map the scene using SDF functions.
vec4 map(vec3 p) {
    vec3 op = p;

    // Wall.
    float wall = sdBox(p + vec3(0.0, 4.15, -0.1), vec3(12.0, 2.0, 1.0));
    vec3 mat;
    wall -= getConcreteMaterial(p, mat);
    mat *= 0.2;

    // Head.
    p.y += 0.08;
    p.z += 0.45;
    float headd = head(p);
    float d = headd;

    // Torso.
    float rough = fbm(p);
    d = smin(d, sdCapsule(p, vec3(0.0, -0.5, 0.0), vec3(0.0, -1.8, 0.0), rough - 0.25), 0.1);

    // Arms.
    p.x = abs(p.x - 0.1);
    const vec3 shoulder = vec3(0.34, -1.2, -0.1);
    const vec3 elbow = vec3(0.44, -1.5, -0.1);
    float w = sign(op.x) > 0.0 ? 0.2 : 0.08;
    float fbm5 = fbm(p * 5.0);
    float upper = sdCapsule(p, shoulder, elbow, fbm5 * w);
    float lower = sdCapsule(p, elbow, vec3(0.34, -1.8, -0.2), fbm5 * w);
    d = smin(d, smin(upper, lower, 0.02), mix(0.1, 0.0, min(distance(p, shoulder) * 2.5, 1.0)));

    // Butt.
    p.x = abs(op.x - 0.04);
    d = smin(d, length(p + vec3(-0.06, 1.95, -0.24)) - fbm5 * 0.3, 0.02);

    // Thighs.
    p.x = abs(op.x - 0.06);
    vec3 hip = vec3(0.12, -1.98, 0.0); 
    vec3 knee = vec3(0.15, -1.98, -0.55); 
    d = smin(d, sdCapsule(p, hip, knee, fbm5 * 0.3), 0.07);

    // Shins.
    vec3 ankleDelta = vec3(0.0, -0.32, 0.0);
    mat2 swing = rot(-max(0.0, abs(0.6 * sin(iTime * 1.5+ sign(op.x) * 0.3))));
    ankleDelta.yz *= swing;
    vec3 ankle = knee + ankleDelta;
    d = min(d, sdRoundCone(p, knee, ankle, fbm5 * 0.3, fbm5 * 0.1));

    // Feet.
    vec3 toeDelta = vec3(0.02, -0.04, -0.1);
    toeDelta.yz *= swing;
    vec3 toe = ankle + toeDelta;
    d = min(d, sdRoundCone(p, ankle, toe, fbm5 * 0.15, fbm5 * 0.1));

    if (d < wall) {
        mat = vec3(0.3, 0.3, 0.35);
    	if (isEye && abs(d - headd) < 0.001) mat = vec3(0.001);
    }

    d = min(d, op.y + 5.0);

    return vec4(min(d, wall), mat);
}

vec3 calcNormal(vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.005773;
    return normalize(e.xyy * map(p + e.xyy).x + 
					 e.yyx * map(p + e.yyx).x + 
					 e.yxy * map(p + e.yxy).x + 
					 e.xxx * map(p + e.xxx).x);
}

float calcShadow(vec3 p, vec3 lightPos) {
    vec3 rd = normalize(lightPos - p);

    float res = 1.0;
    float t = 0.05;
    for(float i = 0.0; i < 45.0; i++)
    {
        float h = map(p + rd * t).x;
        if (h < MIN_DIST * t)
            return 0.0; // Hit an object - Full shadow.

        res = min(res, 48.0 * h / t);
        t += h;

        if (t > 20.0)
            break; // Marched far enough - Stop.
    }

    return res;
}

// Quick ambient occlusion.
float ao(vec3 p, vec3 n, float h) {
    return map(p + h * n).x / h;
}

// Subsurface scattering.
float sss(vec3 p, vec3 ld, float h) {
    return smoothstep(0.0, 1.0, map(p + h * ld).x / h);
}

/**********************************************************************************/

vec3 vignette(vec3 col, vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    col *= 0.5 + 0.5 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.4);
    return col;
}

vec3 back(vec2 uv) {
    return vec3(fbm(vec3(0.0, uv * 20.0)) * 0.02) * (1.0 - uv.y * 2.0);
}

vec3 applyLighting(vec3 p, vec3 rd, vec3 mat) {
    vec3 sunCol = vec3(2.0, 1.6, 1.4);
    vec3 sunPos = vec3(10.0, 5.0, -10.0);
    vec3 sunDir = normalize(sunPos - p);
    vec3 n = calcNormal(p);
    float amb = dot(vec3(ao(p, n, 0.2), ao(p, n, 0.5), ao(p, n, 2.0)), vec3(0.2, 0.3, 0.5));

    // Diffuse color.
    float diff = max(0.0, dot(sunDir, n));
    diff += sss(p, sunDir, 0.5);

    // Primary light (Sun).
    float sha = calcShadow(p, sunPos) + 0.25;
    vec3 lig = diff * sunCol * sha;

    // Specular.
    float spe = pow(max(0.0, dot(rd, reflect(normalize(sunPos - p), n))), 15.0);
    lig += spe;

    // Seconary light (Bounce light);
    lig += max(0.0, dot(-sunDir, n)) * sunCol * 0.1 * amb;

    float fre = 1.0 + dot(rd, n);
    const vec3 back = vec3(0.0);
    return mix(mat.r == 0.001 ? vec3(spe) : mat * lig, back, fre);
}

vec4 march(vec3 ro, vec3 rd, out vec3 p) {
    float d = 0.01, prevd = 1.0;
    vec4 h;
    for (float steps = 0.0; steps < 64.0; steps++) {
        p = ro + rd * d;
        h = map(p);

        if (abs(h.x) < MIN_DIST)
            break; // We've hit a surface - Stop.
        if (d > 20.0) return vec4(1e7);

        d += h.x; // No hit, so keep marching.
    }

    return h;
}

vec3 getSceneColor(vec3 ro, vec3 rd) {
    // Raymarch.
    vec3 p;
  	vec4 hit = march(ro, rd, p);

    if (hit.x > 1e6) return back(rd.xy);

    // Lighting.
    return applyLighting(p, rd, hit.yzw);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Camera.
    float ft = smoothstep(0.0, 1.0, fract(iTime / 12.0));
    float phase = mod(floor(iTime / 12.0), 3.0);
    float dim = 1.0 - pow(abs(cos(iTime * 3.141 / 12.0)), 100.0);

    vec3 ro;
    vec3 lookAt = vec3(0.0, -1.0, 0.0);

    if (phase == 0.0) {
        ro = vec3(0.0, -0.5, mix(-8.0, -4.6, ft)); // -8.0 -4.6
        ro.xz *= rot(mix(1.24, -0.3, ft)); // 1.24, -0.3
    } else if (phase == 1.0) {
        ro = vec3(0.0, mix(2.0, -2.0, ft), mix(-8.0, -4.2, ft)); // -3.9 -8.0
        ro.xz *= rot(mix(-0.43, -0.2, ft)); // -0.2 -0.43
    } else if (phase == 2.0) {
        ro = vec3(0.0, -0.5, mix(-1.0, -4.23, ft)); // -1.0  -4.23
        ro.xz *= rot(mix(2.19, 0.13, ft)); // 2.19 0.13
        lookAt = vec3(0.0, -1.6, 0.0);
    }

    vec3 col = vec3(0.0);
#ifdef AA
    for (float dx = 0.0; dx <= 1.0; dx++) {
        for (float dy = 0.0; dy <= 1.0; dy++) {
            vec2 coord = fragCoord + vec2(dx, dy) * 0.5;
#else
            vec2 coord = fragCoord;
#endif
            vec2 uv = (coord - 0.5 * iResolution.xy) / iResolution.y;

            vec3 rd = getRayDir(ro, lookAt, uv);
            col += getSceneColor(ro, rd);
#ifdef AA
        }
    }
    col /= 4.0;
#endif

    // Output to screen.
    col = vignette(pow(col * dim, vec3(0.4545)), fragCoord);
    fragColor = vec4(col, 1.0);
}

vec3 floatCol(int r, int g, int b) {
	return vec3( float(r)/255., float(g)/255., float(b)/255.);
}

vec4 makeFlag(vec2 p, float w, float h) {
    float mask = 0.;

    float period = sign(p.y) * .008*cos(p.x*20.0 - iTime*5.);

    float hn = h + .015 + period;

    float wn = w + .015 + .008*cos(p.y*20.0 - iTime*5.);
   	mask += (1. - smoothstep(wn, wn+.000001, abs(p.x) )) * (1. - smoothstep(hn, hn+.000001, abs(p.y) ) );
    mask *= clamp(sign(p.x + .2) + 1., 0., 1.);

    vec3 col1 = vec3(.33, .8, .98);
    vec3 col2 = vec3(.96, .65, .72);
    vec3 col3 = vec3(1., 1., 1.);

    float h1 = h - h/4. + period;
    float stripe1Mask = smoothstep( h1, h1+.01, abs(p.y) );
    vec3 flagCol = mix(col2, col1, stripe1Mask);
    float h2 = h/4. + period;
    float stripe2Mask = smoothstep( h2, h2+.01, abs(p.y) );
    flagCol = mix(col3, flagCol, stripe2Mask);

    vec3 col = mix(vec3(0., 0., 0.), flagCol, mask);

    float poleMask = 0.0;
    vec2 pp = p + vec2(w+.015, 0.);
    float l = .014;
    poleMask += (1. - smoothstep(l, l+.001, abs(pp.x) )) * (1. - smoothstep(h+.015+.008, h+.015+.008+.001, pp.y ) );
    poleMask += floor(1.-poleMask) * smoothstep(l+.0001, l, length(pp-vec2(0., 0.17)));

    vec3 brown = floatCol(117, 83, 63);

    col = mix(col, brown, poleMask);
    //col = vec3(poleMask);

    return vec4(col, poleMask+mask);
}

vec3 makeSky(vec2 p) {
	// Make a sky gradient
	vec3 skyTop = floatCol(45, 250, 236);
    vec3 skyBottom = floatCol(197, 240, 237);

   	// Mix the two colors based on height
    vec3 col = mix(skyBottom, skyTop, p.y);

    //Add sun
    vec3 sunColor = floatCol(250, 244, 117);
    float r = .1;
    vec2 sp = p-vec2(.5,.25);
    float sunMask = smoothstep(r+0.05, r, length(sp));

    col = mix(col, sunColor, sunMask);

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 p = uv + vec2(.3, -.1);

 	vec3 col = makeSky(uv);
    vec4 flag = makeFlag(p, 0.2, 0.15);
    col = mix(col, flag.xyz, flag.w);

    fragColor = vec4(col,1.0);
}

//Created by Jaromir Mulders
//www.jaromirmulders.nl

#define M_PI 3.14159265359

float hash21(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }

float noise(vec2 x) {
	vec2 i = floor(x);
	vec2 f = fract(x);

	float a = hash21(i);
	float b = hash21(i + vec2(1.0, 0.0));
	float c = hash21(i + vec2(0.0, 1.0));
	float d = hash21(i + vec2(1.0, 1.0));

	vec2 u = f * f * (3.0 - 2.0 * f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}



float flareLine(vec2 p, vec2 width){

  float sphere = length(p);
  sphere = smoothstep(width.x,0.,sphere);    

  vec2 s = vec2(0.5,1.);

  float line = abs(p.y+smoothstep(s.x,s.y,cos(p.x)*0.2));
  line+= abs(p.y-smoothstep(s.x,s.y,cos(p.x)*0.2));

  line = smoothstep(width.y,0.0,line);
  line*= sphere;  

  return line;
}

float sinSphere(vec2 p, float scale, float offset){

  float s = sin(length(p*scale)+offset);

  return smoothstep(0.98,0.7,s)-smoothstep(1.,0.6,s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;

    uv*=1.2;

    float sLen = length(uv);
    float n = hash21(uv);
    float n2 = noise(uv*15.+iTime*0.08);

    float l1 = flareLine(uv + vec2(0.,0.),
                         vec2(.9,0.03) + vec2(cos(iTime*0.265)) *vec2(0.11,0.01) );
	float l2 = flareLine(uv + vec2(-0.08,0.01),
                         vec2(.5,0.013) + vec2(cos(iTime*0.287)) *vec2(0.12,0.005));
	float l3 = flareLine(uv + vec2(0.09,-0.011),
                         vec2(.7,0.018) + vec2(cos(iTime*0.289)) *vec2(0.13,0.0023));

    float lCurve = cos(uv.x*M_PI*0.6)*0.2;
    float lUv = abs(uv.y+lCurve);
    lUv+= abs(uv.y -lCurve);

    float curveLine = length(lUv);
    curveLine = smoothstep(0.8,0.,curveLine);

    vec2 sUv = uv*1.5;
    float line = smoothstep(.15+n2*0.08,0.,abs(sUv.y));
    float s = length(sUv);
    float sphere = smoothstep(.8,-0.5,s);
    float lAnim = fract(hash21(vec2(iTime*0.1)));
    lAnim = max(0.9,lAnim);
    float wSphere = smoothstep(.1+lAnim*0.03,-0.02,s);
    float d = dot(abs(sUv.x*1.),abs(sUv.y*0.75));
    float star = smoothstep(.07,-0.1,d)*5.;
    float sSphere = line*star*sphere;
    sSphere = pow(sSphere,2.);

    float bulb = length(uv*1.9+n2*-0.015);
    bulb = bulb * smoothstep(1.,0.3,bulb);
    bulb = smoothstep(0.3,.5,bulb);

    vec2 rUv = uv;
    float emit = abs(sin(atan(rUv.y,rUv.x)*.7+.2));
    emit+= abs(sin(atan(rUv.y,-rUv.x)*0.8+.1));
    emit*=0.5;
    emit*=smoothstep(.9,0.,sLen) - bulb*0.3 ;
    emit = pow(emit,2.5);
    emit = (emit*mix(n,1.,0.97))*0.9;

	float rRep = 1.5;
    float rLen = length(uv*2.5);
    float angle = atan(rUv.x,rUv.y);
    float rAngle = sin(angle*rRep+n2*0.9+iTime*0.5);
	float rays = (abs(rAngle))*smoothstep(1.+n2*0.05,0.,rLen);
    rays*= clamp(.1/rLen,0.,1.);
    rays-=smoothstep(.8,0.,rLen)*2.;
    float aId = floor(angle*rRep);
    float rId = hash21(vec2(aId));
	rays = clamp(rays* abs(sin(angle*0.3+0.1))*0.3,0.,1.) ;

    float r1 = sinSphere(uv+n2*0.1,0.9,0.0);
    float r2 = sinSphere(uv+n2*-0.023,1.,0.0);
    float r3 = sinSphere(uv+n2*0.05,1.1,0.0);
    vec3 ripple = vec3(r1,r2,r3)*mix(sin(angle*0.7+iTime*0.23),1.,0.5);


    vec3 col = vec3(0.);

    col+=l1*vec3(0.1,0.1,.3);
    col+=smoothstep(0.3,1.,l1)*vec3(1.)*0.3;

    col+=l2*vec3(0.2,0.5,0.6);
    col+=smoothstep(0.3,1.,l2)*vec3(1.)*0.3;

    col+=l3*vec3(1.5,0.3,1.);
    col+=smoothstep(0.3,1.,l3)*vec3(1.)*0.3;

    col+=vec3(sSphere*1.5)*vec3(.8,.05,0.)*2.;
	col+=vec3(wSphere)*vec3(1.1);

    col+=ripple*0.075;

    col+=rays*vec3(.4,0.3,0.2)*100.;
    col+=mix(vec3(0.34,0.5,0.6),vec3(.9,0.05,0.1),emit)*emit;

    col = pow(col,vec3(0.4545));

    fragColor = vec4(col,1.);
    //fragColor = vec4(ripple,1.);
}

