mat3 TetrahedronBasis;
mat3 InvTetrahedronBasis;
vec3 TetrahedronCenter;
void initTetrahedron(){
  TetrahedronBasis = mat3(
      cos(PI/6.0), sin(PI/6.0), 0,
      cos(PI/6.0), -sin(PI/6.0), 0,
      sqrt(1.0/3.0), 0, sqrt(2.0/3.0)
  );
  InvTetrahedronBasis = mat3(sqrt(1.0/3.0),sqrt(1.0/3.0),0, 1,-1,0, -sqrt(1.0/6.0),-sqrt(1.0/6.0),sqrt(3.0/2.0));
  TetrahedronCenter = (TetrahedronBasis[0]+TetrahedronBasis[1]+TetrahedronBasis[2])/4.0;
}
