vec3[8] getBoundingCuboid(vec3 point){
  vec3 ptf = floor(point), ptc = floor(point + vec3(1.0));
  vec3[8] res;
  res[0] = ptf;
  res[1] = vec3(ptf.x, ptf.y, ptc.z);
  res[2] = vec3(ptf.x, ptc.y, ptf.z);
  res[3] = vec3(ptf.x, ptc.y, ptc.z);
  res[4] = vec3(ptc.x, ptf.y, ptf.z);
  res[5] = vec3(ptc.x, ptf.y, ptc.z);
  res[6] = vec3(ptc.x, ptc.y, ptf.z);
  res[7] = ptc;
  return res;
}


/*
vector rotation
*/
mat2 rotate2D(float angle){
  return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}
vec3 rotateAround(vec3 axis, float angle, vec3 p){
  //Rodrigues' rotation formula
  return p*cos(angle) + axis*dot(axis, p)*(1.0-cos(angle)) + cross(axis, p)*sin(angle);
}
vec3 rotateFromTo(vec3 f, vec3 t, vec3 p){
  vec3 tmp = cross(f,t);
  vec3 axis = normalize(tmp);
  float angle = atan(length(tmp),dot(f,t));
  vec3 res = rotateAround(axis, angle, p);
  return (length(tmp)>0.0)? res : p;
}
vec3 tiltVec(vec3 v, float amp, float dir){
  vec3 tmp = rotateAround(vec3(0,1,0), amp, vec3(1,0,0));
  tmp = rotateAround(vec3(1,0,0), dir, tmp);
  return rotateFromTo(vec3(1,0,0), v, tmp);
}


/*
random generation
 */

int rand_PCG(int seed){
  const highp int increment = 0x4f6f4f3;
  const highp int multiplier = 0x8934649*4+1;
  highp int state = (seed + increment) & 0xffffffff;
  highp int x;
  int count;
  state = (state * multiplier + increment) & 0xffffffff;
  x = state;
  state = (state * multiplier + increment) & 0xffffffff;
  count = (x >> 28)&0x0f;  //32-log2(16)
  x ^= (x >> 10)&0x3fffff;  //(32-(16-log2(16)))/2
  int xs = (x >> 12)&0xffff;  //16-log2(16)
  return ((xs >> count) | (xs << (16-count)))&0xffff;
}
int rand16(float f){
  return rand_PCG(int(floor(f)));
}
float rand_uniform(vec3 v){
  ivec3 sv = ivec3(floor(v));
  int r = rand_PCG(rand_PCG(rand_PCG(sv.x)^sv.y)^sv.z);
  return float(r+1) / float(0x10000);
}
float rand_exponential(vec3 v, float average){
  //inversion method
  return -average*log(1.0-rand_uniform(v));
}
vec2 rand_normal2(vec3 v){
  //Box–Muller's method
  float r1 = rand_uniform(v);
  float r2 = rand_uniform(v.zxy);
  float len = sqrt(-2.0*log(r1));
  float angle = 2.0*PI*r2;
  return len * vec2(cos(angle), sin(angle));
}
float rand_normal(vec3 v){
  return rand_normal2(v).x;
}



