Camera cam; //uniform

vec3 uvToRayDirection(vec2 uv){
  vec2 uvs = 2.0*uv - vec2(1.0);
  vec3 defview = vec3(0.0, 0.0, 1.0);
  vec3 view = normalize(cam.target-cam.position);
  vec3 res = normalize( vec3(tan(cam.angle.x)*uvs.x, tan(cam.angle.y)*uvs.y, 1.0) );
  res.xy = rotate2D(cam.roll)*res.xy;
  res.yz = rotate2D(asin(view.y))*res.yz;
  res.xz = rotate2D(atan(view.x, view.z))*res.xz;
  return res;
}
