/*
fBM noise generation
 */
struct ParamFBM {
  float seed;
  vec3 scale;
  float attenuation;
  float tilt;
};
struct PartFBM {
  float weightSum;
  vec3 vec;
  float scalar;
};
PartFBM newPartFBM(){
  PartFBM res;
  res.weightSum = 0.0;
  res.vec = vec3(0.0);
  res.scalar = 0.0;
  return res;
}
PartFBM normalize(PartFBM src){
  PartFBM res;
  res.weightSum = src.weightSum/src.weightSum;
  res.vec = src.vec/src.weightSum;
  res.scalar = src.scalar/src.weightSum;
  return res;
}

void partNoise(inout PartFBM res, vec3 pt, vec3 pti, ParamFBM param, float amp){
  float angle = rand_uniform(pti*13.0) * 2.0*PI;
  float mag = rand_normal(pti);
  float r = distance(pt, pti);// / sqrt(2.0/3.0);
  float weight = smoothstep(1.0,0.0, r*r);
  float val = amp*weight*mag;
  res.weightSum += weight;
  res.vec += tiltVec(vec3(1,0,0)*val, param.tilt*mag, angle);
  res.scalar += val;
}
void tetraNoise(inout PartFBM res, vec3 p, ParamFBM param, float freq, float amp){
  float salt = float(rand16(freq + param.seed));
  vec3 pt = InvTetrahedronBasis*(p/(freq*param.scale) + salt);
  PartFBM part = newPartFBM();
  vec3[8] pts = getBoundingCuboid(pt);
  for (int i=min(0,iFrame); i<8; i++) {
    partNoise(part, pt, pts[i], param, amp);
  }
  part = normalize(part);
  res.vec += part.vec;
  res.scalar += part.scalar;
}
PartFBM fbmNoise(vec3 point, ParamFBM param){
  int maxOctave = int(ceil( log2(length(param.scale)/getPixelSize(point)) ));
  float freq = 1.0;
  float amp = 1.0;
  PartFBM sum = newPartFBM();
  for (int octave=0; octave <= maxOctave; octave++){
    tetraNoise(sum, point, param, freq, amp);
    freq /= 2.0;
    amp /= param.attenuation;
  }
  return sum;
}


/*
cellular noise generation
 */
struct CellParam{
  vec3 shift;
  vec3 seed;
  vec3 scale;
  float randomness;
};
struct CellInfo{
  vec3 center;
  float rCenter;
  float rBoundary;
};
void cellularNoiseSub(vec3 p, CellParam param, vec3 center, inout float r1, inout float r2, inout CellInfo res){
  vec3 rands;
  rands.x = rand_normal(center+param.seed.xxx);
  rands.y = rand_uniform(center+param.seed.yyy);
  rands.z = rand_uniform(center+param.seed.zzz);
  vec3 randv = param.randomness*rands.x *tiltVec(vec3(1,0,0), rands.y*PI, rands.z*PI*2.0);
  vec3 cellp = center+randv;
  float r = distance(p, cellp);
  r2 = (r2<r)? r2 : r;
  res.center = (r1<r)? res.center : cellp;
  r2 = (r1<r)? r2 : r1;
  r1 = (r1<r)? r1 : r;
}
CellInfo cellularNoise(vec3 point, CellParam param){
  vec3 p = point/param.scale + param.shift;
  CellInfo res;
  float r1=200.0, r2=200.0;
  vec3[8] pts = getBoundingCuboid(InvTetrahedronBasis*p);
  for (int i=min(0,iFrame); i<8; i++) {
    cellularNoiseSub(p, param, TetrahedronBasis*pts[i], r1, r2, res);
  }
  res.center = (res.center-param.shift)*param.scale;
  res.rCenter = r1;
  res.rBoundary = (r2-r1)/2.0;
  return res;
}


/*
grouth ring generation
 */
struct GrowthRingParam{
  float scale;
  vec3 center;
  vec3 direction;
  float sharpness;
  float randomness;
  ParamFBM pFBM;
};
float GrowthRingNoise(vec3 point, GrowthRingParam param) {
  vec3 pt = point/param.scale - param.center;
  vec3 p = rotateFromTo(param.direction, vec3(1,0,0), pt);
  float r = length( p.yz );
  r += param.randomness * fbmNoise(p, param.pFBM).scalar;
  float v = fract(r);
  float val = (v*v) * smoothstep(0.0, 1.0, param.sharpness*(1.0-v));
  return val;
}



