}

Surface raycast(vec3 origin, vec3 direction) {
  const float e = 0.00001;
  const float rayMaxLength = 100.0;
  const int maxIteration = 50;
  vec3 point = origin;
  DistanceToSurface nearest = findNearestSurface(point);
  for (int i=0; (i<maxIteration) && (distance(point, origin) < rayMaxLength) && (nearest.distance >= e); i++) {
    point += direction * nearest.distance;
    nearest = findNearestSurface(point);
  }
  Surface res = getSurface(nearest.objectID, point);
  res.objectID = (nearest.distance < e)? res.objectID : -1;
  return res;
}


/*
physics based rendering
 */

float Vt_GGX(Surface suf, vec3 v){
  return 0.5*(-1.0 + sqrt(1.0 + pow(suf.roughness,2.0)*(1.0/pow(dot(v,suf.normal),2.0)-1.0)));
}
vec3 applyDirectionalLight(Surface suf, vec3 light, vec3 lightDir, vec3 viewDir){
  vec3 halfDir = normalize(lightDir+viewDir);
  float F0 = suf.metalness; // Fresnel reflection rate
  float F = F0 + (1.0-F0)*pow(1.0-dot(viewDir,halfDir), 5.0);
  float G = 1.0/(1.0 + Vt_GGX(suf, lightDir) + Vt_GGX(suf, viewDir));
  float D = pow(suf.roughness, 2.0) /PI /pow(pow(dot(suf.normal, halfDir),2.0) * (pow(suf.roughness,2.0)-1.0) +1.0, 2.0);
  vec3 specular = vec3(1.0) * D*G*F / (4.0* dot(suf.normal, lightDir) * dot(suf.normal, viewDir));
  vec3 diffuse = suf.albedo/PI;
  vec3 color = (diffuse*(1.0-suf.metalness) + specular*suf.metalness) * dot(lightDir, suf.normal) * light;
  return max(color, 0.0);
}

vec3 applyAmbientLight(Surface suf, vec3 light){
  vec3 diffuse = suf.albedo/PI;
  return (1.0-suf.occlusion) * diffuse * light;
}



