main
 */

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  initTetrahedron();

  float radius = 4.0*cos(iTime/5.0) + 12.0;
  cam.position = vec3(radius*cos(iTime/2.0), 4.0, radius*sin(iTime/2.0));
  cam.target = vec3(0.0, 0.0, 0.0);
  cam.roll = 0.0/360.0 * 2.0*PI;
  cam.angle.x = 2.0*PI * 50.0/360.0;
  cam.angle.y = atan(tan(cam.angle.x)*(iResolution.y/iResolution.x));

  vec2 uv = fragCoord/iResolution.xy;
  vec3 rayDir = uvToRayDirection(uv);
  Surface suf = raycast(cam.position, rayDir);

  vec3 col = vec3(0.0);
  col += (suf.objectID<0)? vec3(0.0) : applyDirectionalLight(suf, vec3(2.0), -vec3(0.0, -1.0, 0.0), -rayDir);
  col += (suf.objectID<0)? vec3(0.0) : applyAmbientLight(suf, vec3(0.5));

  fragColor = vec4(col, 1.0);
}

