float Perlin(vec2 p) {                        // Perlin noise
    vec2 I = floor(p), 
         F = fract(p), 
     //  U = F;
     //  U = F*F*(3.-2.*F);                   // based Perlin noise
         U = F*F*F*( F* ( F*6.-15.) + 10. );  // improved Perlin noise ( better derivatives )
    return mix( L(0,U.x) , L(1,U.x) , U.y );  // smooth interpolation of corners random wavelets
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         S = 8. / R.yy,
         U = ( 2.*u - R ) * S,
         D;
    float v = Perlin( U );                    
 // O = vec4( .5 + .5 * v ); return;          // see Perlin noise

                                              // --- divergence-free displacement noise
 // D = vec2( v - Perlin( U+vec2(0,S.y) ), Perlin( U+vec2(S.x,0) ) - v ) *2./ S;
    D = vec2( -dFdy(v), dFdx(v) ) / S;        // using hardware derivatives
 // O = vec4( .5 + .5 * D, 0, 0); return;     // see noise vector
 // O = vec4(.5+.5*atan(D.y,D.x)/3.14); return;// see noise direction
 // D += .5*U; O = vec4(.5+.5*atan(D.y,D.x)/3.14); return;// see noise direction

    v = length( U + 1.*D );                   // distorted distance
 // O = vec4( v / 10. );                      // see
 // O = vec4( v / max(2.,1.5*length(U)) );    // renormalize by distance
    O = vec4( v / max(1.5,.5*dot(U,U)) );     // renormalize by distance then div by dist
 // O = vec4( .5 + .5 * sin(v) );             // distorted concentric circles
 // O = vec4( .01* v / fwidth(v) );
    O *= vec4(1.3,.8,.5,0);                   // coloring
}

void mainImage( out vec4 O, in vec2 f )
{
    vec2 uv = f / iResolution.y,
        u = vec2(fract(uv.x * 3. - iTime * .2), uv.y * 1.6 - .3);
    if(u.y > .5){
        u.x = abs(u.x - .5);
    }
    float k = length(u - .5),
        e = smoothstep(k, k * .99, .5);
    vec3 a = vec3(1.,0.,0.),
        b = u.y > .5 ? vec3(1.) : vec3(0.,0.,.2);
    O = vec4(mix(a,b,e),1.);
}

